 from DateTime import DateTime
 today_date = DateTime()

 def create_movement(CD_head_dict, CD_line_list):
 # Head creation
 movement_head = context.newContent( portal_type = 'Monetary Reception'
 , reindex_object = 1
 , start_date = today_date
 , comment = CD_head_dict['start_date']
 , destination = CD_head_dict['destination']
 , activate_kw = {"tag" : import_tag}
 )

 # Container Creation
 base_list=('emission_letter', 'variation', 'cash_status')
 category_list = ('emission_letter/' + CD_head_dict['emission_letter'],
 'variation/' + CD_head_dict['variation'],
 'cash_status/'+CD_head_dict['cash_status'] )

 resource_total_quantity = 0
 for CD_line in CD_line_list:
 # create cash container
 movement_container = movement_head.newContent(portal_type = 'Cash Container Item'
 , reindex_object = 1
 , reference = CD_line['reference']
 , cash_number_range_start = CD_line['range_start']
 , cash_number_range_stop = CD_line['range_stop']
 , activate_kw = {"tag" : import_tag}
 )
 # create a cash container line
 container_line = movement_container.newContent(portal_type = 'Container Line'
 , reindex_object = 1
 , resource_value =CD_head_dict['resource']
 , quantity = CD_line['quantity']
 , activate_kw = {"tag" : import_tag}
 )

 #container_line.setResourceValue(CD_head_dict['resource'])
 container_line.setVariationCategoryList(category_list)
 container_line.updateCellRange(script_id='CashDetail_asCellRange',base_id="movement")
 for key in container_line.getCellKeyList(base_id='movement'):
 if isSameSet(key,category_list):
 cell = container_line.newCell(activate_kw = {"tag" : import_tag}, *key)
 cell.setCategoryList(category_list)
 cell.setQuantity(CD_line['quantity'])
 cell.setMappedValuePropertyList(['quantity','price'])
 cell.setMembershipCriterionBaseCategoryList(base_list)
 cell.setMembershipCriterionCategoryList(category_list)
 cell.edit(force_update = 1,
 price = container_line.getResourceValue().getBasePrice(),
 activate_kw = {"tag" : import_tag})


 resource_total_quantity += CD_line['quantity']
 container_line.getPrice() # Store "price" property on line when creating it, to avoid getting conflicts when reindexing.

 # once all cash containter created, create the movement line for the total
 movement_line = movement_head.newContent(id = "movement"
 , portal_type = 'Cash Delivery Line'
 , resource_value = CD_head_dict['resource']
 , quantity_unit_value = context.portal_categories.quantity_unit.unit
 , activate_kw = {"tag" : import_tag}
 )
 movement_line.setVariationBaseCategoryList(base_list)
 movement_line.setVariationCategoryList(category_list)
 movement_line.updateCellRange(script_id="CashDetail_asCellRange", base_id="movement")
 for key in movement_line.getCellKeyList(base_id='movement'):
 if isSameSet(key,category_list):
 cell = movement_line.newCell(activate_kw = {"tag" : import_tag}, *key)
 cell.setCategoryList(category_list)
 cell.setQuantity(resource_total_quantity)
 cell.setMappedValuePropertyList(['quantity','price'])
 cell.setMembershipCriterionBaseCategoryList(base_list)
 cell.setMembershipCriterionCategoryList(category_list)
 cell.edit(force_update = 1,
 price = movement_line.getResourceValue().getBasePrice(),
 activate_kw = {"tag" : import_tag})

 return {'error' : False, 'message':message_dict['00']+CD_head_dict['reference'],'delivery_id':movement_head.getId()}


 def split_head(CD_file_line_list):

 head_movement_id = CD_file_line_list[14][55:61]
 head_start_date_string = CD_file_line_list[7][35:43]
 head_resource_string = CD_file_line_list[11][32:56]
 head_emission_letter = CD_file_line_list[14][25:26]

 # find letter in the emission_letter category
 default_emissionLetter_list = [x[1] for x in context.portal_categories.emission_letter.getCategoryChildTitleItemList()[1:]]
 head_emission_letter = string.lower(head_emission_letter)
 if not head_emission_letter in default_emissionLetter_list :
 return {'error':True, 'message':message_dict['04'], 'head_dict':{}}

 # Movement ID verification
 # Verify if Movement Id exist
 head_movement_id = (head_movement_id or '').strip()
 if head_movement_id == '':
 return {'error':True, 'message':message_dict['02'], 'head_dict':{}}

 # Verify if Movement Id is not already exist in the database
 head_movement_id = head_movement_id+'_'+head_emission_letter

 # date verification
 try:
 head_start_date_string = '20' + head_start_date_string[6:8] + \
 '/' + head_start_date_string[3:5] + \
 '/' + head_start_date_string[0:2] + ' 0:0am'
 head_start_date = DateTime(head_start_date_string )
 except:
 return {'error':True, 'message':message_dict['01'], 'head_dict':{}}

 head_resource_string = (head_resource_string or '').strip()
 head_resource_string = string.replace(head_resource_string,' ','')
 if head_resource_string == '':
 return {'error':True, 'message':message_dict['09'], 'head_dict':{}}

 try:
 head_resource_variation_string = head_resource_string[-4:]
 head_resource_variation = int(head_resource_variation_string)
 if head_resource_variation <= 0 :
 return {'error':True, 'message':message_dict['10'], 'head_dict':{}}
 except:
 return {'error':True, 'message':message_dict['10'], 'head_dict':{}}

 try:
 column_end_price = head_resource_string.find('F')
 if column_end_price < 0:
 return {'error':True, 'message':message_dict['11'], 'head_dict':{}}
 head_resource_price = int(head_resource_string[:column_end_price ])
 if head_resource_price <= 0 :
 return {'error':True, 'message':message_dict['11'], 'head_dict':{}}
 except:
 return {'error':True, 'message':message_dict['11'], 'head_dict':{}}


 head_resource_list = context.portal_catalog(portal_type = ('Banknote','Coin'))

 head_resource = None
 test = []
 for i_resource in head_resource_list:
 resource_object = i_resource.getObject()
 if (resource_object.getPriceCurrency() == 'currency_module/%s' % reference_currency_id) \
 and (resource_object.getBasePrice() == head_resource_price):
 head_resource = resource_object
 break

 resource_variation_list = head_resource.getVariationList()
 # get variation from resource
 head_variation = str(head_resource_variation)
 if head_variation not in resource_variation_list:
 head_variation = max(resource_variation_list)

 if head_resource is None:
 return {'error':True, 'message':message_dict['12'], 'head_dict':{}}

 # Define destination
 user_site_list = context.Baobab_getUserAssignedRootSiteList()
 if user_site_list is None:
 return {'error':True, 'message':message_dict['13'], 'head_dict':{}}

 head_destination = '%s/%s' % (user_site_list[0],'caveau/serre')
 if head_emission_letter in context.Baobab_getUserEmissionLetterList():
 head_destination = '%s/%s' % (head_destination,'encaisse_des_billets_neufs_non_emis')
 else:
 site = context.Baobab_getEmissionLetterSiteValue(emission_letter=head_emission_letter)
 head_destination = '%s/%s/%s' % (head_destination,
 'encaisse_des_billets_neufs_non_emis_en_transit_allant_a',site.getId())


 CD_head_dict = {'reference' : head_movement_id
 ,'start_date' : head_start_date
 ,'resource' : head_resource
 ,'currency' : head_resource.getPriceCurrency()
 ,'emission_letter' : head_emission_letter
 ,'variation' : head_variation
 ,'cash_status' : 'new_not_emitted'
 ,'destination' : head_destination
 }

 return {'error':False, 'message':None, 'head_dict':CD_head_dict}


 def split_lines(CD_file_line_list):
 CD_line_list = []
 treated_container_list = []
 line_number = 0
 reference_list = []
 ref_append = reference_list.append
 for CD_line in CD_file_line_list:
 line_number += 1
