 new_CD_line = string.replace(new_CD_line, '\r', '')
 line_container_id = new_CD_line[:7]
 line_range_start = new_CD_line[34:46]
 line_range_stop = new_CD_line[49:61]
 line_quantity_string = new_CD_line[64:70]

 line_container_id = (line_container_id or '').strip()
 if line_container_id == '':
 return {'error':True, 'message':'Ligne ' + str(line_number) + ' : ' + message_dict['05'],'lines_dict':{}}
 if line_container_id in treated_container_list :
 return {'error':True, 'message':'Ligne ' + str(line_number) + ' : ' + message_dict['06'],'lines_dict':{}}

 line_quantity_string = (line_quantity_string or '').strip()
 line_quantity_string = string.replace(line_quantity_string,' ','')
 try:
 line_quantity = int(line_quantity_string)
 if line_quantity <= 0 :
 return {'error':True, 'message':'Ligne ' + str(line_number) + ' : ' + message_dict['07'],'lines_dict':{}}
 except:
 return {'error':True, 'message':'Ligne ' + str(line_number) + ' : ' + message_dict['07'],'lines_dict':{}}
 treated_container_list.append(line_container_id)

 CD_line_list.append({'reference' : line_container_id
 ,'range_start' : line_range_start
 ,'range_stop' : line_range_stop
 ,'quantity' : line_quantity}
 )
 ref_append(line_container_id)


