 def url_redirection(error_message,delivery_id=None):
 if delivery_id is not None:
 redirect_url = '%s?%s' % ( context.absolute_url()+'/'+delivery_id+'/'+'view', 'portal_status_message=' + error_message)
 else:
 redirect_url = '%s?%s' % ( context.absolute_url()+'/'+'view', 'portal_status_message=' + error_message)
 request[ 'RESPONSE' ].redirect( redirect_url )


