 raise ValueError, split_head_result_dict['message']
 else:
 return url_redirection(split_head_result_dict['message'])

 # check remaining activities tag is based on destination site and resource
 destination = split_head_result_dict['head_dict']['destination']
 destination_value = context.portal_categories.site.restrictedTraverse(destination)
 # we serialize the destination to be sure we won't do the same operation twice at the same time
 destination_value.serialize()
 destination_uid = destination_value.getUid()
 resource_value = split_head_result_dict['head_dict']['resource']

 import_tag = "%s_monetary_reception_%s" %(destination_uid, resource_value.getRelativeUrl())
 if context.portal_activities.countMessageWithTag(import_tag) != 0:
 if batch == 1:
 raise ValueError, "Some activities are remaining from previous import, please try later."
 else:
 return url_redirection("Some activities are remaining from previous import, please try later.")


