value.string:
"""
 This script is an alpha version of what should be a generic OpenOffice importer,
 which is a collection of scripts and UI to import lots of objects to ERP5
 from a spreadsheet. The idea is to let the user map each spreadsheet column with
 one portal type property.

 Other files related to this feature:
 * openoffice-erp5-objects-template.sxc -- A template that describe persons to help you create your own data source
 * ERP5Site_importObjectFromOOoFastInput -- The UI
 * ERP5Site_getSpreadsheetColumnNameList -- Utility script
 * ERP5Site_getModulePortalTypeSortedPropertyList -- Utility script
 * ERP5Site_importObjectFromOOoActivity -- Activating Import Script
 """

 request = context.REQUEST

 def getRequestKeys(rq):
 return map(lambda x: x[0], rq.items())

 def getRequestValues(rq):
 return map(lambda x: x[1], rq.items())

 def getSpreadsheet():
 from Products.ERP5OOo.OOoUtils import OOoParser, CorruptedOOoFile
 OOoParser = OOoParser()
 # Extract tables from the speadsheet file
 import_file = kw['import_file']
 OOoParser.openFile(import_file)
 return OOoParser.getSpreadsheetsMapping()


 # if listbox is empty, then we are in the first step
 listbox = []
 if kw.has_key('listbox') and kw['listbox'] not in ('', None):
 listbox = kw['listbox']


 # First step
 if len(listbox) == 0:
 spreadsheets = getSpreadsheet()
 # Put the result of OOo parsing in the request
 request.set('ooo_import_spreadsheet_data', spreadsheets)
 # Set a flag in the request to differentiate the two steps
 request.set('OOOIMPORT_STEP_TWO', True)
 request.set('my_import_file', None)

 # Second Step
 else:
 # Re-upload the OOo file
 spreadsheets = getSpreadsheet()

 # Build the data mapping
 mapping = {}
 request.set('ooo_import_spreadsheet_data', spreadsheets)

 listbox_ordered_lines = context.ERP5Site_getSpreadsheetColumnNameList()
 for line in listbox_ordered_lines:
 listbox_id = line.getUid()[4:]
 portal_type_property = None
 if same_type(listbox, dict):
 portal_type_property = listbox[listbox_id]['portal_type_property_list']
 else:
 for listbox_dict in listbox:
 if listbox_dict['listbox_key'] == listbox_id:
 portal_type_property = listbox_dict['portal_type_property_list']
 break
 if portal_type_property not in ('', None):
 spreadsheet_name = getattr(line, 'spreadsheet_name')
 column_name = getattr(line, 'spreadsheet_column')
 map_item = { 'portal_type': portal_type_property.split('.')[0]
 , 'property' : portal_type_property.split('.')[1]
 }
 if spreadsheet_name not in mapping:
 mapping[spreadsheet_name] = {}
 mapping[spreadsheet_name][column_name] = map_item

 # Convert each spreadsheet
 for (sheet_name, sheet_data) in spreadsheets.items():

 # The ERP5 module where all lines of the current spreadsheet will be generated
 sheet_module = context

 # Build a data structure to associate column index with column title
 column_index = {}
 for column_id in xrange(len(sheet_data[0])):
 column_index[column_id] = sheet_data[0][column_id]

 # Build a data structure to associate column index with object property and portal type
 column_mapping = {}
 for (column_name, property_dict) in mapping.get(sheet_name, {}).items():
 for (column_id, column_title) in column_index.items():
 if column_name == column_title:
 column_mapping[column_id] = property_dict
 break

 # Create a dict to describe each line in property
 object_list = {}
 for line in sheet_data[1:]:
 new_object_property_dict = {}
 for line_property_index in xrange(len(line)):
 if line_property_index in column_mapping:
 property_module = sheet_module
 property_module_url = property_module.getRelativeUrl()
 property_pt = column_mapping[line_property_index]['portal_type']
 property_id = column_mapping[line_property_index]['property']
 property_value = line[line_property_index]

 if not property_value in ('', None):
 if not object_list.has_key(property_module_url):
 object_list[property_module_url] = {}
 if not object_list[property_module_url].has_key(property_pt):
 object_list[property_module_url][property_pt] = []

 # Create a new property value
 new_object_property_dict[property_id] = property_value

 if new_object_property_dict:
 object_list[property_module_url][property_pt].append(new_object_property_dict)

 context.activate(tag="object_OOo_import", priority=1, activity="SQLQueue").ERP5Site_importObjectFromOOoActivity(object_list=object_list)

 return request.RESPONSE.redirect(context.absolute_url() + '/view?portal_status_message=OpenOffice+document+importing.')

 return context.ERP5Site_importObjectFromOOoFastInput(REQUEST=request)
