value.string:
context_obj = context.getObject()

 feature_module_type = 'Feature Module'
 feature_document_type = 'Feature Document'
 feature_type = 'Feature'

 if context_obj.getPortalType() == feature_module_type:
 # we are in a module, so create a feature document
 feature_doc = context_obj.newContent( portal_type = feature_document_type
 , title = kw['feature_document_title']
 , description = kw['feature_document_description']
 )
 destination_obj = feature_doc
 elif context_obj.getPortalType() in (feature_document_type, feature_type):
 destination_obj = context_obj
 else:
 return context.REQUEST.RESPONSE.redirect(context.absolute_url() + '?portal_status_message=Error:+bad+context.')

 # this list contain all features items
 features_items = []

 # get the user information
 for feature_line in listbox:
 if feature_line.has_key('listbox_key'):
 feature_line_id = int(feature_line['listbox_key'])
 feature = {}
 feature['id'] = feature_line_id
 feature['title'] = feature_line['feature_title']
 feature['sub_title'] = feature_line['sub_feature_title']
 feature['sub_description'] = feature_line['sub_feature_description']
 features_items.append(feature)

 # sort the features list by id to have the same order of the user
 features_items.sort(lambda x, y: cmp(x['id'], y['id']))

 clean_features = {}
 has_1st_level_feature = False
 has_2nd_level_feature = False
 new_1st_level_feature = None

 # scan every fast input line to create a structured and comprehensive list of features and sub-features
 for feature_item in features_items:
 # the item has a first level feature
 if feature_item['title'] not in ('', None):
 has_1st_level_feature = True
 new_1st_level_feature = []
 new_1st_level_feature_title = feature_item['title']
 else:
 has_1st_level_feature = False

 # the item has a second level feature, built it
 if feature_item['sub_title'] not in ('', None):
 has_2nd_level_feature = True
 new_2nd_level_feat = {}
 new_2nd_level_feat['title'] = feature_item['sub_title']
 if feature_item['sub_title'] not in ('', None):
 new_2nd_level_feat['description'] = feature_item['sub_description']
 else:
 new_2nd_level_feat['description'] = None
 else:
 has_2nd_level_feature = False

 if has_2nd_level_feature == True and new_1st_level_feature != None:
 new_1st_level_feature.append(new_2nd_level_feat)

 if has_1st_level_feature == True:
 if clean_features.has_key(new_1st_level_feature_title):
 new_1st_level_feature = clean_features[new_1st_level_feature_title] + new_1st_level_feature
 clean_features[new_1st_level_feature_title] = new_1st_level_feature

 # create feature objects and sub-features
 for key in clean_features.keys():
 new_1st_feature = destination_obj.newContent( portal_type = feature_type
 , title = key
 )
 for second_level in clean_features[key]:
 new_2nd_feature = new_1st_feature.newContent( portal_type = feature_type
 , title = second_level['title']
 , description = second_level['description']
 )

 # return to the feature module
 return context.REQUEST.RESPONSE.redirect(context.absolute_url() + '?portal_status_message=Feature+document+added.')
