value.string:
"""TODO: support arguments (checkboxes from dialog)
 add checkbox for search_in_skins
 """

 from Products.PythonScripts.standard import html_quote

 result_list = []


 if search_in_skins:
 for script_container in (context.portal_skins, context.portal_workflow):
 for script_path, script in context.ZopeFind(script_container, obj_metatypes=['Script (Python)'], search_sub=1):
 last_match = 4 # XXX this last_match thing does not work. The idea is to show only one match if, say, the first 3 lines have a match
 line_list = script.document_src().splitlines()
 max_line_len = len(line_list)
 for line_number, line_text in enumerate(line_list):
 if search_string in line_text:
 if (line_number - last_match) >= 4:
 result_list.append(dict(
 url="%s/manage_main?line=%s" % (script.absolute_url(), line_number-8),
 name=script.absolute_url(),
 start_line=line_number-8,
 match=("\n".join(line_list[max(0, line_number-4):min(line_number+4, max_line_len)]))
 .replace(search_string, "<mark>%s</mark>" % html_quote(search_string)),
 ))
 last_match = line_number


 for component in context.contentValues():
 if component.getValidationState() not in ('validated', 'modified'):
 continue
 text_content = component.getTextContent() or ''
 last_match = 4
 line_list = text_content.splitlines()
 max_line_len = len(line_list)

 for line_number, line_text in enumerate(line_list):
 if search_string in line_text:
 if (line_number - last_match) >= 4:
 result_list.append(dict(
 url="%s?line=%s" % (component.absolute_url(), line_number+1),
 name=component.getReference(),
 start_line=line_number +1,
 match=("\n".join(line_list[max(0, line_number-4):min(line_number+4, max_line_len)]))
 .replace(search_string, "<mark>%s</mark>" % html_quote(search_string)),
 ))
 last_match = line_number

 return result_list
