// Browser gadget toggle section
 function toggleSection(section_id, image_id){
 section_obj = getElement(section_id);
 image_obj = getElement(image_id);
 if(section_obj.style.display=='none'){
 section_obj.style.display='block';
 image_obj.src='images/tree_open.png';}
 else{
 section_obj.style.display='none';
 image_obj.src='images/tree_closed.png';}
 } 

 // Pop up window for KM
 function delayedDocumentDetailsPopupWindowLoad(dom_element, url){
 dom_id = dom_element.id;
 if (dom_element.style.display=='block'){
 d = MochiKit.Async.doSimpleXMLHttpRequest(url);
 d.addCallback(handleServerSuccess);
 }};

 function handleServerSuccess(res){
 dom_element.innerHTML = res.responseText;};

 function toggleDocumentDetailsPopupWindow(dom_id, show, url){
 dom_element = getElement(dom_id);
 remote_url = url;
 if (show==true){
 dom_element.style.display='block';
 setTimeout('delayedDocumentDetailsPopupWindowLoad(dom_element, remote_url);', 500)
 }
 else{
 dom_element.style.display='none';
 }
 }

 MochiKit.DOM.addLoadEvent(setScreenClass);
 connect(window, "onresize", setScreenClass);

 function setScreenClass(){
 var fmt = document.documentElement.clientWidth;
 var cls = (fmt<=800)?'screen_small':(fmt>800)?'screen_wide':'screen_small';
 document.body.className=cls;
 };
