 this._changedProps.put_DStrikeout(field.getValue() == "checked");
 }
 this.checkGroup = 0;
 if (field.getValue() == "checked") {
 this.checkGroup = 1;
 this.chStrike.setValue(0);
 if (this._changedProps) {
 this._changedProps.put_Strikeout(false);
 }
 this.checkGroup = 0;
 }
 if (this.api && !this._noApply) {
