                mnuTableMerge, mnuTableSplit, {\r\n
                    caption: "--"\r\n
                },\r\n
                menuTableCellAlign, {\r\n
                    caption: "--"\r\n
                },\r\n
                menuTableWrapInline, menuTableWrapFlow, {\r\n
                    caption: "--"\r\n
                },\r\n
                menuTableAdvanced, {\r\n
                    caption: "--"\r\n
                },\r\n
                menuAddCommentTable, menuAddHyperlinkTable, menuHyperlinkTable, menuHyperlinkSeparator, menuParagraphTable]\r\n
            }).on("hide:after", function (menu) {\r\n
                me.fireEvent("editcomplete", me);\r\n
                me.currentMenu = null;\r\n
            });\r\n
            var menuParagraphBreakBefore = new Common.UI.MenuItem({\r\n
                caption: me.breakBeforeText,\r\n
                checkable: true\r\n
            }).on("click", function (item, e) {\r\n
                me.api.put_PageBreak(item.checked);\r\n
            });\r\n
            var menuParagraphKeepLines = new Common.UI.MenuItem({\r\n
                caption: me.keepLinesText,\r\n
                checkable: true\r\n
            }).on("click", function (item, e) {\r\n
                me.api.put_KeepLines(item.checked);\r\n
            });\r\n
            var paragraphVAlign = function (item, e) {\r\n
                if (me.api) {\r\n
                    var properties = new CImgProperty();\r\n
                    properties.put_VerticalTextAlign(item.options.valign);\r\n
                    me.api.ImgApply(properties);\r\n
                }\r\n
            };\r\n
            var menuParagraphVAlign = new Common.UI.MenuItem({\r\n
                caption: me.vertAlignText,\r\n
                menu: new Common.UI.Menu({\r\n
                    menuAlign: "tl-tr",\r\n
                    items: [me.menuParagraphTop = new Common.UI.MenuItem({\r\n
                        caption: me.topCellText,\r\n
                        checkable: true,\r\n
                        checked: false,\r\n
                        toggleGroup: "popupparagraphvalign",\r\n
                        valign: c_oAscVerticalTextAlign.TEXT_ALIGN_TOP\r\n
                    }).on("click", _.bind(paragraphVAlign, me)), me.menuParagraphCenter = new Common.UI.MenuItem({\r\n
                        caption: me.centerCellText,\r\n
                        checkable: true,\r\n
                        checked: false,\r\n
                        toggleGroup: "popupparagraphvalign",\r\n
                        valign: c_oAscVerticalTextAlign.TEXT_ALIGN_CTR\r\n
                    }).on("click", _.bind(paragraphVAlign, me)), me.menuParagraphBottom = new Common.UI.MenuItem({\r\n
                        caption: me.bottomCellText,\r\n
                        checkable: true,\r\n
                        checked: false,\r\n
                        toggleGroup: "popupparagraphvalign",\r\n
                        valign: c_oAscVerticalTextAlign.TEXT_ALIGN_BOTTOM\r\n
                    }).on("click", _.bind(paragraphVAlign, me))]\r\n
                })\r\n
            });\r\n
            var menuParagraphAdvanced = new Common.UI.MenuItem({\r\n
                caption: me.advancedParagraphText\r\n
            }).on("click", _.bind(me.advancedParagraphClick, me));\r\n
            var menuFrameAdvanced = new Common.UI.MenuItem({\r\n
                caption: me.advancedFrameText\r\n
            }).on("click", _.bind(me.advancedFrameClick, me));\r\n
            var menuCommentSeparatorPara = new Common.UI.MenuItem({\r\n
                caption: "--"\r\n
            });\r\n
            var menuAddCommentPara = new Common.UI.MenuItem({\r\n
                caption: me.addCommentText\r\n
            }).on("click", _.bind(me.addComment, me));\r\n
            var menuHyperlinkParaSeparator = new Common.UI.MenuItem({\r\n
                caption: "--"\r\n
            });\r\n
            var menuAddHyperlinkPara = new Common.UI.MenuItem({\r\n
                caption: me.hyperlinkText\r\n
            }).on("click", _.bind(me.addHyperlink, me));\r\n
            var menuEditHyperlinkPara = new Common.UI.MenuItem({\r\n
                caption: me.editHyperlinkText\r\n
            }).on("click", _.bind(me.editHyperlink, me));\r\n
            var menuRemoveHyperlinkPara = new Common.UI.MenuItem({\r\n
                caption: me.removeHyperlinkText\r\n
            }).on("click", function (item, e) {\r\n
                me.api.remove_Hyperlink();\r\n
                me.fireEvent("editcomplete", me);\r\n
            });\r\n
            var menuHyperlinkPara = new Common.UI.MenuItem({\r\n
                caption: me.hyperlinkText,\r\n
                menu: new Common.UI.Menu({\r\n
                    menuAlign: "tl-tr",\r\n
                    items: [menuEditHyperlinkPara, menuRemoveHyperlinkPara]\r\n
                })\r\n
            });\r\n
            me.menuSpellPara = new Common.UI.MenuItem({\r\n
                caption: me.loadSpellText,\r\n
                disabled: true\r\n
            });\r\n
            me.menuSpellMorePara = new Common.UI.MenuItem({\r\n
                caption: me.moreText,\r\n
                menu: new Common.UI.Menu({\r\n
                    menuAlign: "tl-tr",\r\n
                    style: "max-height: 300px;",\r\n
                    items: []\r\n
                })\r\n
            });\r\n
            me.langParaMenu = new Common.UI.MenuItem({\r\n
                caption: me.langText,\r\n
                menu: new Common.UI.Menu({\r\n
                    menuAlign: "tl-tr",\r\n
                    maxHeight: 300,\r\n
                    items: []\r\n
                }).on("show:after", function (menu) {})\r\n
            });\r\n
            var menuIgnoreSpellPara = new Common.UI.MenuItem({\r\n
                caption: me.ignoreSpellText\r\n
            }).on("click", function (item, e) {\r\n
                me.api.asc_ignoreMisspelledWord(me._currentSpellObj, false);\r\n
                me.fireEvent("editcomplete", me);\r\n
            });\r\n
            var menuIgnoreAllSpellPara = new Common.UI.MenuItem({\r\n
                caption: me.ignoreAllSpellText\r\n
            }).on("click", function (item, e) {\r\n
                me.api.asc_ignoreMisspelledWord(me._currentSpellObj, true);\r\n
                me.fireEvent("editcomplete", me);\r\n
            });\r\n
            var menuIgnoreSpellParaSeparator = new Common.UI.MenuItem({\r\n
                caption: "--"\r\n
            });\r\n
            var menuSpellcheckParaSeparator = new Common.UI.MenuItem({\r\n
                caption: "--"\r\n
            });\r\n
            var menuParaCopy = new Common.UI.MenuItem({\r\n
                caption: me.textCopy,\r\n
