            }).on("click", _.bind(me.onCutCopyPaste, me));\r\n
            var menuParaCut = new Common.UI.MenuItem({\r\n
                caption: me.textCut,\r\n
                value: "cut"\r\n
            }).on("click", _.bind(me.onCutCopyPaste, me));\r\n
            this.textMenu = new Common.UI.Menu({\r\n
                initMenu: function (value) {\r\n
                    var isInShape = (value.imgProps && value.imgProps.value && !_.isNull(value.imgProps.value.get_ShapeProperties()));\r\n
                    var isInChart = (value.imgProps && value.imgProps.value && !_.isNull(value.imgProps.value.get_ChartProperties()));\r\n
                    menuParagraphVAlign.setVisible(isInShape && !isInChart);\r\n
                    if (isInShape || isInChart) {\r\n
                        var align = value.imgProps.value.get_VerticalTextAlign();\r\n
                        me.menuParagraphTop.setChecked(align == c_oAscVerticalTextAlign.TEXT_ALIGN_TOP);\r\n
                        me.menuParagraphCenter.setChecked(align == c_oAscVerticalTextAlign.TEXT_ALIGN_CTR);\r\n
                        me.menuParagraphBottom.setChecked(align == c_oAscVerticalTextAlign.TEXT_ALIGN_BOTTOM);\r\n
                    }\r\n
                    menuParagraphAdvanced.isChart = (value.imgProps && value.imgProps.isChart);\r\n
                    menuParagraphBreakBefore.setVisible(!isInShape && !isInChart);\r\n
                    menuParagraphKeepLines.setVisible(!isInShape && !isInChart);\r\n
                    if (value.paraProps) {\r\n
                        menuParagraphBreakBefore.setChecked(value.paraProps.value.get_PageBreakBefore());\r\n
                        menuParagraphKeepLines.setChecked(value.paraProps.value.get_KeepLines());\r\n
                    }\r\n
                    var text = null;\r\n
                    if (me.api) {\r\n
                        text = me.api.can_AddHyperlink();\r\n
                    }\r\n
                    menuCommentSeparatorPara.setVisible(!isInChart && me.api.can_AddQuotedComment() !== false && me.mode.canCoAuthoring && me.mode.canComments);\r\n
                    menuAddCommentPara.setVisible(!isInChart && me.api.can_AddQuotedComment() !== false && me.mode.canCoAuthoring && me.mode.canComments);\r\n
                    menuAddCommentPara.setDisabled(value.paraProps && value.paraProps.locked === true);\r\n
                    me.menuSpellPara.setVisible(value.spellProps !== undefined && value.spellProps.value.get_Checked() === false);\r\n
                    menuSpellcheckParaSeparator.setVisible(value.spellProps !== undefined && value.spellProps.value.get_Checked() === false);\r\n
                    menuIgnoreSpellPara.setVisible(value.spellProps !== undefined && value.spellProps.value.get_Checked() === false);\r\n
                    menuIgnoreAllSpellPara.setVisible(value.spellProps !== undefined && value.spellProps.value.get_Checked() === false);\r\n
                    me.langParaMenu.setVisible(value.spellProps !== undefined && value.spellProps.value.get_Checked() === false);\r\n
                    menuIgnoreSpellParaSeparator.setVisible(value.spellProps !== undefined && value.spellProps.value.get_Checked() === false);\r\n
                    if (value.spellProps !== undefined && value.spellProps.value.get_Checked() === false && value.spellProps.value.get_Variants() !== null && value.spellProps.value.get_Variants() !== undefined) {\r\n
                        me.addWordVariants(true);\r\n
                    } else {\r\n
                        me.menuSpellPara.setCaption(me.loadSpellText);\r\n
                        me.clearWordVariants(true);\r\n
                        me.menuSpellMorePara.setVisible(false);\r\n
                    }\r\n
                    if (me.langParaMenu.isVisible() && me._currLang.id !== me._currLang.paraid) {\r\n
                        me.changeLanguageMenu(me.langParaMenu.menu);\r\n
                        me._currLang.paraid = me._currLang.id;\r\n
                    }\r\n
                    menuAddHyperlinkPara.setVisible(value.hyperProps === undefined && text !== false);\r\n
                    menuHyperlinkPara.setVisible(value.hyperProps !== undefined);\r\n
                    menuHyperlinkParaSeparator.setVisible(menuAddHyperlinkPara.isVisible() || menuHyperlinkPara.isVisible());\r\n
                    menuEditHyperlinkPara.hyperProps = value.hyperProps;\r\n
                    if (text !== false) {\r\n
                        menuAddHyperlinkPara.hyperProps = {};\r\n
                        menuAddHyperlinkPara.hyperProps.value = new CHyperlinkProperty();\r\n
                        menuAddHyperlinkPara.hyperProps.value.put_Text(text);\r\n
                    }\r\n
                    var disabled = value.paraProps.locked || (value.headerProps !== undefined && value.headerProps.locked);\r\n
                    menuAddHyperlinkPara.setDisabled(disabled);\r\n
                    menuHyperlinkPara.setDisabled(disabled || value.hyperProps !== undefined && value.hyperProps.isSeveralLinks === true);\r\n
                    menuParagraphBreakBefore.setDisabled(disabled || !_.isUndefined(value.headerProps) || !_.isUndefined(value.imgProps));\r\n
                    menuParagraphKeepLines.setDisabled(disabled);\r\n
                    menuParagraphAdvanced.setDisabled(disabled);\r\n
                    menuFrameAdvanced.setDisabled(disabled);\r\n
                    menuParagraphVAlign.setDisabled(disabled);\r\n
                    var cancopy = me.api && me.api.can_CopyCut();\r\n
                    menuParaCopy.setDisabled(!cancopy);\r\n
                    menuParaCut.setDisabled(disabled || !cancopy);\r\n
                    menuParaPaste.setDisabled(disabled);\r\n
                    menuFrameAdvanced.setVisible(value.paraProps.value.get_FramePr() !== undefined);\r\n
                },\r\n
                items: [me.menuSpellPara, me.menuSpellMorePara, menuSpellcheckParaSeparator, menuIgnoreSpellPara, menuIgnoreAllSpellPara, me.langParaMenu, menuIgnoreSpellParaSeparator, menuParaCut, menuParaCopy, menuParaPaste, {\r\n
                    caption: "--"\r\n
                },\r\n
                menuParagraphBreakBefore, menuParagraphKeepLines, menuParagraphVAlign, menuParagraphAdvanced, menuFrameAdvanced, menuCommentSeparatorPara, menuAddCommentPara, menuHyperlinkParaSeparator, menuAddHyperlinkPara, menuHyperlinkPara]\r\n
            }).on("hide:after", function (menu, e) {\r\n
                if (me.suppressEditComplete) {\r\n
                    me.suppressEditComplete = false;\r\n
                    return;\r\n
                }\r\n
                me.fireEvent("editcomplete", me);\r\n
                me.currentMenu = null;\r\n
            });\r\n
            var menuEditHeaderFooter = new Common.UI.MenuItem({\r\n
                caption: me.editHeaderText\r\n
            });\r\n
            this.hdrMenu = new Common.UI.Menu({\r\n
                initMenu: function (value) {\r\n
                    menuEditHeaderFooter.setCaption(value.Header ? me.editHeaderText : me.editFooterText);\r\n
                    menuEditHeaderFooter.off("click").on("click", function (item) {\r\n
                        if (me.api) {\r\n
                            if (value.Header) {\r\n
                                me.api.GoToHeader(value.PageNum);\r\n
                            } else {\r\n
                                me.api.GoToFooter(value.PageNum);\r\n
                            }\r\n
                            me.fireEvent("editcomplete", me);\r\n
                        }\r\n
                    });\r\n
                },\r\n
                items: [menuEditHeaderFooter]\r\n
            }).on("hide:after", function (menu) {\r\n
                me.fireEvent("editcomplete", me);\r\n
                me.currentMenu = null;\r\n
            });\r\n
            var nextpage = $("#id_buttonNextPage");\r\n
            nextpage.attr("data-toggle", "tooltip");\r\n
            nextpage.tooltip({\r\n
                title: me.textNextPage + Common.Utils.String.platformKey("Alt+PgDn"),\r\n
                placement: "top-right"\r\n
            });\r\n
            var prevpage = $("#id_buttonPrevPage");\r\n
            prevpage.attr("data-toggle", "tooltip");\r\n
            prevpage.tooltip({\r\n
                title: me.textPrevPage + Common.Utils.String.platformKey("Alt+PgUp"),\r\n
                placement: "top-right"\r\n
            });\r\n
        },\r\n
        setLanguages: function (langs) {\r\n
            var me = this;\r\n
            if (langs && langs.length > 0) {\r\n
                _.each(langs, function (lang, index) {\r\n
                    me.langParaMenu.menu.addItem(new Common.UI.MenuItem({\r\n
                        caption: Common.util.LanguageInfo.getLocalLanguageName(lang.asc_getId())[1],\r\n
                        checkable: true,\r\n
                        toggleGroup: "popupparalang",\r\n
                        langid: lang.asc_getId()\r\n
                    }).on("click", function (item, e) {\r\n
                        if (me.api) {\r\n
                            if (!_.isUndefined(item.options.langid)) {\r\n
                                me.api.put_TextPrLang(item.options.langid);\r\n
                            }\r\n
                            me._currLang.paraid = item.options.langid;\r\n
                            me.langParaMenu.menu.currentCheckedItem = item;\r\n
                            me.fireEvent("editcomplete", me);\r\n
                        }\r\n
                    }));\r\n
                    me.langTableMenu.menu.addItem(new Common.UI.MenuItem({\r\n
                        caption: Common.util.LanguageInfo.getLocalLanguageName(lang.asc_getId())[1],\r\n
                        checkable: true,\r\n
                        toggleGroup: "popuptablelang",\r\n
                        langid: lang.asc_getId()\r\n
                    }).on("click", function (item, e) {\r\n
                        if (me.api) {\r\n
                            if (!_.isUndefined(item.options.langid)) {\r\n
                                me.api.put_TextPrLang(item.options.langid);\r\n
                            }\r\n
                            me._currLang.tableid = item.options.langid;\r\n
                            me.langTableMenu.menu.currentCheckedItem = item;\r\n
                            me.fireEvent("editcomplete", me);\r\n
                        }\r\n
                    }));\r\n
                });\r\n
                me.langTableMenu.menu.doLayout();\r\n
                me.langParaMenu.menu.doLayout();\r\n
            }\r\n
        },\r\n
        focus: function () {\r\n
            var me = this;\r\n
            _.defer(function () {\r\n
                me.cmpEl.focus();\r\n
            },\r\n
            50);\r\n
        },\r\n
