 var c_paragraphLinerule = {\r\n
    LINERULE_LEAST: 0,\r\n
    LINERULE_AUTO: 1,\r\n
    LINERULE_EXACT: 2\r\n
};\r\n
var c_pageNumPosition = {\r\n
    PAGE_NUM_POSITION_TOP: 1,\r\n
    PAGE_NUM_POSITION_BOTTOM: 2,\r\n
    PAGE_NUM_POSITION_RIGHT: 0,\r\n
    PAGE_NUM_POSITION_LEFT: 1,\r\n
    PAGE_NUM_POSITION_CENTER: 2\r\n
};\r\n
var c_tableWrap = {\r\n
    TABLE_WRAP_NONE: 0,\r\n
    TABLE_WRAP_PARALLEL: 1\r\n
};\r\n
var c_tableAlign = {\r\n
    TABLE_ALIGN_LEFT: 0,\r\n
    TABLE_ALIGN_CENTER: 1,\r\n
    TABLE_ALIGN_RIGHT: 2\r\n
};\r\n
var c_tableBorder = {\r\n
    BORDER_VERTICAL_LEFT: 0,\r\n
    BORDER_HORIZONTAL_TOP: 1,\r\n
    BORDER_VERTICAL_RIGHT: 2,\r\n
    BORDER_HORIZONTAL_BOTTOM: 3,\r\n
    BORDER_VERTICAL_CENTER: 4,\r\n
    BORDER_HORIZONTAL_CENTER: 5,\r\n
    BORDER_INNER: 6,\r\n
    BORDER_OUTER: 7,\r\n
