define(\'documenteditor/main/app/controller/DocumentHolder\',["core", "documenteditor/main/app/view/DocumentHolder"], function () {\r\n
    DE.Controllers.DocumentHolder = Backbone.Controller.extend({\r\n
        models: [],\r\n
        collections: [],\r\n
        views: ["DocumentHolder"],\r\n
        initialize: function () {},\r\n
        onLaunch: function () {\r\n
            this.documentHolder = this.createView("DocumentHolder").render();\r\n
            this.documentHolder.el.tabIndex = -1;\r\n
        },\r\n
        setApi: function (api) {\r\n
            this.api = api;\r\n
        },\r\n
        createDelayedElements: function () {\r\n
            var diagramEditor = this.getApplication().getController("Common.Controllers.ExternalDiagramEditor").getView("Common.Views.ExternalDiagramEditor");\r\n
            if (diagramEditor) {\r\n
                diagramEditor.on("internalmessage", _.bind(function (cmp, message) {\r\n
                    var command = message.data.command;\r\n
                    var data = message.data.data;\r\n
                    if (this.api) {\r\n
                        (diagramEditor.isEditMode()) ? this.api.asc_editChartDrawingObject(data) : this.api.asc_addChartDrawingObject(data);\r\n
                    }\r\n
                },\r\n
                this));\r\n
                diagramEditor.on("hide", _.bind(function (cmp, message) {\r\n
                    this.documentHolder.fireEvent("editcomplete", this.documentHolder);\r\n
                    if (this.api) {\r\n
                        this.api.asc_enableKeyEvents(true);\r\n
                    }\r\n
                },\r\n
                this));\r\n
            }\r\n
        }\r\n
    });\r\n
});\n
