 				// This may raise an exception which is actually
 				// handled in jQuery.ajax (so no try/catch here)
 				xhr.send( options.hasContent && options.data || null );
 			},
 			abort: function() {
 				if ( callback ) {
 					callback();
 				}
 			}
 		};
 	}
