 fontColorPicker.select(clr, true);
 isselected = true;
 break;
 }
 }
 if (!isselected) {
 fontColorPicker.clearSelection();
 }
 } else {
 fontColorPicker.select(clr, true);
 }
 this._state.clrtext = clr;
 }
 this._state.clrtext_asccolor = color;
 }
