 var SCALE_MIN = 40;
 var MENU_SCALE_PART = 260;
 define('spreadsheeteditor/main/app/view/RightMenu',["text!spreadsheeteditor/main/app/template/RightMenu.template", "jquery", "underscore", "backbone", "common/main/lib/component/Button", "common/main/lib/component/MetricSpinner", "common/main/lib/component/CheckBox", "spreadsheeteditor/main/app/view/ParagraphSettings", "spreadsheeteditor/main/app/view/ImageSettings", "spreadsheeteditor/main/app/view/ChartSettings", "spreadsheeteditor/main/app/view/ShapeSettings", "common/main/lib/component/Scroller"], function (menuTemplate, $, _, Backbone) {
 SSE.Views.RightMenu = Backbone.View.extend(_.extend({
 el: "#right-menu",
 template: _.template(menuTemplate),
 events: {},
 initialize: function () {
 this.minimizedMode = true;
 this.btnText = new Common.UI.Button({
 hint: this.txtParagraphSettings,
 asctype: c_oAscTypeSelectElement.Paragraph,
 enableToggle: true,
 disabled: true,
 toggleGroup: "tabpanelbtnsGroup"
 });
