 function replaceWords(template, words) {
 var word, value, tpl = template;
 for (word in words) {
 if (undefined !== word) {
 value = words[word];
 tpl = tpl.replace(new RegExp(word, "g"), value);
 }
 }
 return tpl;
 }
 Common.Views.CommentsPopover = Common.UI.Window.extend({
