 }))
 });
 this.commentsView.on("item:click", function (picker, item, record, e) {
 var btn, showEditBox, showReplyBox, commentId, replyId, hideAddReply;
 function readdresolves() {
 me.renderResolvedComboButtons();
 t.renderResolvedComboButtons();
 me.update();
 }
 btn = $(e.target);
 if (btn) {
 showEditBox = record.get("editTextInPopover");
 showReplyBox = record.get("showReplyInPopover");
 hideAddReply = record.get("hideAddReply");
 commentId = record.get("uid");
 replyId = btn.attr("data-value");
 if (record.get("hint")) {
 t.fireEvent("comment:disableHint", [record]);
 return;
 }
 if (btn.hasClass("btn-edit")) {
 if (!_.isUndefined(replyId)) {
 t.fireEvent("comment:closeEditing", [commentId]);
 t.fireEvent("comment:editReply", [commentId, replyId, true]);
 this.replyId = replyId;
 this.autoHeightTextBox();
 me.calculateSizeOfContent();
 me.setLeftTop(me.arrowPosX, me.arrowPosY, me.leftX);
 me.calculateSizeOfContent();
 readdresolves();
 me.hookTextBox();
 this.autoScrollToEditButtons();
 this.setFocusToTextBox();
 } else {
 if (!showEditBox) {
 t.fireEvent("comment:closeEditing");
 record.set("editTextInPopover", true);
 t.fireEvent("comment:show", [commentId]);
 this.autoHeightTextBox();
 me.calculateSizeOfContent();
 me.setLeftTop(me.arrowPosX, me.arrowPosY, me.leftX);
 me.calculateSizeOfContent();
 readdresolves();
 me.hookTextBox();
 this.autoScrollToEditButtons();
 this.setFocusToTextBox();
 }
 }
 } else {
 if (btn.hasClass("btn-delete")) {
 if (!_.isUndefined(replyId)) {
 t.fireEvent("comment:removeReply", [commentId, replyId]);
 me.calculateSizeOfContent();
 me.setLeftTop(me.arrowPosX, me.arrowPosY, me.leftX);
 me.calculateSizeOfContent();
 } else {
 t.fireEvent("comment:remove", [commentId]);
 }
 t.fireEvent("comment:closeEditing");
 readdresolves();
 } else {
 if (btn.hasClass("user-reply")) {
 t.fireEvent("comment:closeEditing");
 record.set("showReplyInPopover", true);
 me.calculateSizeOfContent();
 me.setLeftTop(me.arrowPosX, me.arrowPosY, me.leftX);
 me.calculateSizeOfContent();
 readdresolves();
 this.autoHeightTextBox();
 me.hookTextBox();
 this.autoScrollToEditButtons();
 this.setFocusToTextBox();
 } else {
 if (btn.hasClass("btn-reply", false)) {
 if (showReplyBox) {
 this.clearTextBoxBind();
 t.fireEvent("comment:addReply", [commentId, this.getActiveTextBoxVal()]);
 t.fireEvent("comment:closeEditing");
 readdresolves();
 }
 } else {
 if (btn.hasClass("btn-close", false)) {
 t.fireEvent("comment:closeEditing", [commentId]);
 me.calculateSizeOfContent();
 t.fireEvent("comment:show", [commentId]);
 readdresolves();
 } else {
 if (btn.hasClass("btn-inner-edit", false)) {
 if (record.get("dummy")) {
 t.fireEvent("comment:addDummyComment", [this.getActiveTextBoxVal()]);
 return;
 }
 this.clearTextBoxBind();
 if (!_.isUndefined(this.replyId)) {
 t.fireEvent("comment:changeReply", [commentId, this.replyId, this.getActiveTextBoxVal()]);
 this.replyId = undefined;
 t.fireEvent("comment:closeEditing");
 } else {
 if (showEditBox) {
 t.fireEvent("comment:change", [commentId, this.getActiveTextBoxVal()]);
 t.fireEvent("comment:closeEditing");
 me.calculateSizeOfContent();
 }
 }
 readdresolves();
 } else {
 if (btn.hasClass("btn-inner-close", false)) {
 if (record.get("dummy")) {
 me.hide();
 return;
 }
 if (hideAddReply && this.getActiveTextBoxVal().length > 0) {
 me.saveText();
 record.set("hideAddReply", false);
 this.getTextBox().val(me.textVal);
 this.autoHeightTextBox();
 } else {
 this.clearTextBoxBind();
 t.fireEvent("comment:closeEditing", [commentId]);
 }
 this.replyId = undefined;
 me.calculateSizeOfContent();
 me.setLeftTop(me.arrowPosX, me.arrowPosY, me.leftX);
 me.calculateSizeOfContent();
 readdresolves();
 } else {
 if (btn.hasClass("btn-resolve", false)) {
 t.fireEvent("comment:resolve", [commentId]);
 readdresolves();
 }
 }
 }
 }
 }
 }
 }
 }
 }
 });
 me.on({
 "show": function () {
 me.commentsView.autoHeightTextBox();
 var text = me.$window.find("textarea");
 if (text && text.length) {
 text.focus();
 }
 text.keydown(function (event) {
 if (event.keyCode == Common.UI.Keys.ESC) {
 me.hide();
 }
 });
 }
 });
 }
 }
 },
 show: function (animate, loadText, focus, showText) {
 this.options.animate = animate;
 var me = this,
 textBox = this.commentsView.getTextBox();
 if (loadText && this.textVal) {
 textBox && textBox.val(this.textVal);
 }
 if (showText && showText.length) {
 textBox && textBox.val(showText);
 }
 Common.UI.Window.prototype.show.call(this);
 this.renderResolvedComboButtons();
 if (this.commentsView.scroller) {
 this.commentsView.scroller.update({
 minScrollbarLength: 40
 });
 }
 this.hookTextBox();
 },
 hide: function () {
 if (this.handlerHide) {
 this.handlerHide();
 }
 Common.UI.Window.prototype.hide.call(this);
 if (!_.isUndefined(this.e) && this.e.keyCode == Common.UI.Keys.ESC) {
 this.e.preventDefault();
 this.e.stopImmediatePropagation();
 this.e = undefined;
 }
 },
 update: function () {
 if (this.commentsView && this.commentsView.scroller) {
 this.commentsView.scroller.update({
 minScrollbarLength: 40
 });
 }
 },
 isVisible: function () {
 return (this.$window && this.$window.is(":visible"));
 },
 setLeftTop: function (posX, posY, leftX, loadInnerValues) {
 if (!this.$window) {
 this.render();
 }
 if (loadInnerValues) {
 posX = this.arrowPosX;
 posY = this.arrowPosY;
 leftX = this.leftX;
 }
 if (_.isUndefined(posX) && _.isUndefined(posY)) {
 return;
 }
 this.arrowPosX = posX;
 this.arrowPosY = posY;
 this.leftX = leftX;
 var commentsView = $("#id-comments-popover"),
 arrowView = $("#id-comments-arrow"),
 editorView = $("#editor_sdk"),
 editorBounds = null,
 sdkBoundsHeight = 0,
 sdkBoundsTop = 0,
 sdkBoundsLeft = 0,
 sdkPanelRight = "",
 sdkPanelRightWidth = 0,
 sdkPanelLeft = "",
 sdkPanelLeftWidth = 0,
 sdkPanelThumbs = "",
 sdkPanelThumbsWidth = 0,
 sdkPanelTop = "",
 sdkPanelHeight = 0,
 leftPos = 0,
 windowWidth = 0,
 outerHeight = 0,
 topPos = 0,
 sdkBoundsTopPos = 0;
 if (commentsView && arrowView && editorView && editorView.get(0)) {
 editorBounds = editorView.get(0).getBoundingClientRect();
 if (editorBounds) {
 sdkBoundsHeight = editorBounds.height - this.sdkBounds.padding * 2;
 this.$window.css({
 maxHeight: sdkBoundsHeight + "px"

;
 this.btnAbout.panel = (new Common.Views.About({
 el: $("#about-menu-panel"),
 appName: "Spreadsheet Editor"
 })).render();
 return this;
 },
 onBtnMenuToggle: function (btn, state) {
 if (state) {
 this.btnFile.pressed && this.fireEvent("file:show", this);
 btn.panel["show"]();
 this.$el.width(SCALE_MIN);
 if (this.btnSearch.isActive()) {
 this.btnSearch.toggle(false);
 }
 } else {
 (this.btnFile.id == btn.id) && this.fireEvent("file:hide", this);
 btn.panel["hide"]();
 }
 if (this.mode.isEdit) {
 SSE.getController("Toolbar").DisableToolbar(state == true);
 }
 Common.NotificationCenter.trigger("layout:changed", "leftmenu");
 },
 onBtnMenuClick: function (btn, e) {
 this.btnFile.toggle(false);
 this.btnAbout.toggle(false);
 if (btn.options.action == "search") {} else {
 if (btn.pressed) {
 if (! (this.$el.width() > SCALE_MIN)) {
 this.$el.width(localStorage.getItem("sse-mainmenu-width") || MENU_SCALE_PART);
 }
 } else {
 localStorage.setItem("sse-mainmenu-width", this.$el.width());
 this.$el.width(SCALE_MIN);
 }
 }
 Common.NotificationCenter.trigger("layout:changed", "leftmenu");
 },
 onCoauthOptions: function (e) {
 if (this.mode.canCoAuthoring) {
 if (this.mode.canComments) {
 this.panelComments[this.btnComments.pressed ? "show" : "hide"]();
 this.fireEvent((this.btnComments.pressed) ? "comments:show": "comments:hide", this);
 }
 if (this.mode.canChat) {
 if (this.btnChat.pressed) {
 if (this.btnChat.$el.hasClass("notify")) {
 this.btnChat.$el.removeClass("notify");
 }
 this.panelChat.show();
 this.panelChat.focus();
 } else {
 this.panelChat["hide"]();
 }
 }
 }
 },
 setOptionsPanel: function (name, panel) {
 if (name == "chat") {
 this.panelChat = panel.render("#left-panel-chat");
 } else {
 if (name == "comment") {
 this.panelComments = panel;
 }
 }
 },
 markCoauthOptions: function (opt) {
 if (this.btnChat.isVisible() && !this.btnChat.isDisabled() && !this.btnChat.pressed) {
 this.btnChat.$el.addClass("notify");
 }
 },
 close: function (menu) {
 this.btnFile.toggle(false);
 this.btnAbout.toggle(false);
 this.$el.width(SCALE_MIN);
 if (this.mode.canCoAuthoring) {
 if (this.mode.canComments) {
 this.panelComments["hide"]();
 if (this.btnComments.pressed) {
 this.fireEvent("comments:hide", this);
 }
 this.btnComments.toggle(false, true);
 }
 if (this.mode.canChat) {
 this.panelChat["hide"]();
 this.btnChat.toggle(false, true);
 }
 }
 },
 isOpened: function () {
 var isopened = this.btnFile.pressed || this.btnSearch.pressed; ! isopened && (isopened = this.btnComments.pressed || this.btnChat.pressed);
 return isopened;
 },
 disableMenu: function (menu, disable) {
 this.btnFile.setDisabled(false);
 this.btnAbout.setDisabled(false);
 this.btnSupport.setDisabled(false);
 this.btnSearch.setDisabled(false);
 this.btnComments.setDisabled(false);
 this.btnChat.setDisabled(false);
 },
 showMenu: function (menu) {
 var re = /^(\w+):?(\w*)$/.exec(menu);
 if (re[1] == "file") {
 if (!this.btnFile.pressed) {
 this.btnFile.toggle(true);
 this.btnFile.$el.focus();
 }
 this.btnFile.panel.show(re[2].length ? re[2] : undefined);
 } else {
 if (menu == "chat") {
 if (this.btnChat.isVisible() && !this.btnChat.isDisabled() && !this.btnChat.pressed) {
 this.btnChat.toggle(true);
 this.onBtnMenuClick(this.btnChat);
 this.onCoauthOptions();
 this.panelChat.focus();
 }
 } else {
 if (menu == "comments") {
 if (this.btnComments.isVisible() && !this.btnComments.isDisabled() && !this.btnComments.pressed) {
 this.btnComments.toggle(true);
 this.onBtnMenuClick(this.btnComments);
 this.onCoauthOptions();
 this.btnComments.$el.focus();
 }
 }
 }
 }
 },
 getMenu: function (type) {
 switch (type) {
 case "file":
 return this.btnFile.panel;
 case "about":
 return this.btnAbout.panel;
 default:
 return null;
 }
 },
 setMode: function (mode) {
 this.mode = mode;
 return this;
 },
 tipComments: "Comments",
 tipChat: "Chat",
 tipAbout: "About",
 tipSupport: "Feedback & Support",
 tipFile: "File",
 tipSearch: "Search"
 },
 SSE.Views.LeftMenu || {}));
 });
