value.string:
//\n
// Dropdown menus\n
// --------------------------------------------------\n
\n
\n
// Dropdown arrow/caret\n
.caret {\n
  display: inline-block;\n
  width: 0;\n
  height: 0;\n
  margin-left: 2px;\n
  vertical-align: middle;\n
  border-top:   @caret-width-base solid;\n
  border-right: @caret-width-base solid transparent;\n
  border-left:  @caret-width-base solid transparent;\n
}\n
\n
// The dropdown wrapper (div)\n
.dropdown {\n
  position: relative;\n
}\n
\n
// Prevent the focus on the dropdown toggle when closing dropdowns\n
.dropdown-toggle:focus {\n
  outline: 0;\n
}\n
\n
// The dropdown menu (ul)\n
.dropdown-menu {\n
  position: absolute;\n
  top: 100%;\n
  left: 0;\n
  z-index: @zindex-dropdown;\n
  display: none; // none by default, but block on "open" of the menu\n
  float: left;\n
  min-width: 160px;\n
  padding: 5px 0;\n
  margin: 2px 0 0; // override default ul\n
  list-style: none;\n
  font-size: @font-size-base;\n
  background-color: @dropdown-bg;\n
  border: 1px solid @dropdown-fallback-border; // IE8 fallback\n
  border: 1px solid @dropdown-border;\n
  border-radius: @border-radius-base;\n
  .box-shadow(0 6px 12px rgba(0,0,0,.175));\n
  background-clip: padding-box;\n
\n
  // Aligns the dropdown menu to right\n
  &.pull-right {\n
    right: 0;\n
    left: auto;\n
  }\n
\n
  // Dividers (basically an hr) within the dropdown\n
  .divider {\n
    .nav-divider(@dropdown-divider-bg);\n
  }\n
\n
  // Links within the dropdown menu\n
  > li > a {\n
    display: block;\n
    padding: 3px 20px;\n
    clear: both;\n
    font-weight: normal;\n
    line-height: @line-height-base;\n
    color: @dropdown-link-color;\n
    white-space: nowrap; // prevent links from randomly breaking onto new lines\n
  }\n
}\n
\n
// Hover/Focus state\n
.dropdown-menu > li > a {\n
  &:hover,\n
  &:focus {\n
    text-decoration: none;\n
    color: @dropdown-link-hover-color;\n
    background-color: @dropdown-link-hover-bg;\n
  }\n
}\n
\n
// Active state\n
.dropdown-menu > .active > a {\n
  &,\n
  &:hover,\n
  &:focus {\n
    color: @dropdown-link-active-color;\n
    text-decoration: none;\n
    outline: 0;\n
    background-color: @dropdown-link-active-bg;\n
  }\n
}\n
\n
// Disabled state\n
//\n
// Gray out text and ensure the hover/focus state remains gray\n
\n
.dropdown-menu > .disabled > a {\n
  &,\n
  &:hover,\n
  &:focus {\n
    color: @dropdown-link-disabled-color;\n
  }\n
}\n
// Nuke hover/focus effects\n
.dropdown-menu > .disabled > a {\n
  &:hover,\n
  &:focus {\n
    text-decoration: none;\n
    background-color: transparent;\n
    background-image: none; // Remove CSS gradient\n
    .reset-filter();\n
    cursor: not-allowed;\n
  }\n
}\n
\n
// Open state for the dropdown\n
.open {\n
  // Show the menu\n
  > .dropdown-menu {\n
    display: block;\n
  }\n
\n
  // Remove the outline when :focus is triggered\n
  > a {\n
    outline: 0;\n
  }\n
}\n
\n
// Dropdown section headers\n
.dropdown-header {\n
  display: block;\n
  padding: 3px 20px;\n
  font-size: @font-size-small;\n
  line-height: @line-height-base;\n
  color: @dropdown-header-color;\n
}\n
\n
// Backdrop to catch body clicks on mobile, etc.\n
.dropdown-backdrop {\n
  position: fixed;\n
  left: 0;\n
  right: 0;\n
  bottom: 0;\n
  top: 0;\n
  z-index: @zindex-dropdown - 10;\n
}\n
\n
// Right aligned dropdowns\n
.pull-right > .dropdown-menu {\n
  right: 0;\n
  left: auto;\n
}\n
\n
// Allow for dropdowns to go bottom up (aka, dropup-menu)\n
//\n
// Just add .dropup after the standard .dropdown class and you\'re set, bro.\n
// TODO: abstract this so that the navbar fixed styles are not placed here?\n
\n
.dropup,\n
.navbar-fixed-bottom .dropdown {\n
  // Reverse the caret\n
  .caret {\n
    border-top: 0;\n
    border-bottom: @caret-width-base solid;\n
    content: "";\n
  }\n
  // Different positioning for bottom up menu\n
  .dropdown-menu {\n
    top: auto;\n
    bottom: 100%;\n
    margin-bottom: 1px;\n
  }\n
}\n
\n
\n
// Component alignment\n
//\n
// Reiterate per navbar.less and the modified component alignment there.\n
\n
@media (min-width: @grid-float-breakpoint) {\n
  .navbar-right {\n
    .dropdown-menu {\n
      .pull-right > .dropdown-menu();\n
    }\n
  }\n
}\n
\n
