value.string:
//\n
// Grid system\n
// --------------------------------------------------\n
\n
// Set the container width, and override it for fixed navbars in media queries\n
.container {\n
  .container-fixed();\n
\n
  @media (min-width: @screen-sm) {\n
    width: @container-sm;\n
  }\n
  @media (min-width: @screen-md) {\n
    width: @container-md;\n
  }\n
  @media (min-width: @screen-lg-min) {\n
    width: @container-lg;\n
  }\n
}\n
\n
// mobile first defaults\n
.row {\n
  .make-row();\n
}\n
\n
// Common styles for small and large grid columns\n
.make-grid-columns();\n
\n
\n
// Extra small grid\n
//\n
// Columns, offsets, pushes, and pulls for extra small devices like\n
// smartphones.\n
\n
.make-grid-columns-float(xs);\n
.make-grid(@grid-columns, xs, width);\n
.make-grid(@grid-columns, xs, pull);\n
.make-grid(@grid-columns, xs, push);\n
.make-grid(@grid-columns, xs, offset);\n
\n
\n
// Small grid\n
//\n
// Columns, offsets, pushes, and pulls for the small device range, from phones\n
// to tablets.\n
\n
@media (min-width: @screen-sm-min) {\n
  .make-grid-columns-float(sm);\n
  .make-grid(@grid-columns, sm, width);\n
  .make-grid(@grid-columns, sm, pull);\n
  .make-grid(@grid-columns, sm, push);\n
  .make-grid(@grid-columns, sm, offset);\n
}\n
\n
\n
// Medium grid\n
//\n
// Columns, offsets, pushes, and pulls for the desktop device range.\n
\n
@media (min-width: @screen-md-min) {\n
  .make-grid-columns-float(md);\n
  .make-grid(@grid-columns, md, width);\n
  .make-grid(@grid-columns, md, pull);\n
  .make-grid(@grid-columns, md, push);\n
  .make-grid(@grid-columns, md, offset);\n
}\n
\n
\n
// Large grid\n
//\n
// Columns, offsets, pushes, and pulls for the large desktop device range.\n
\n
@media (min-width: @screen-lg-min) {\n
  .make-grid-columns-float(lg);\n
  .make-grid(@grid-columns, lg, width);\n
  .make-grid(@grid-columns, lg, pull);\n
  .make-grid(@grid-columns, lg, push);\n
  .make-grid(@grid-columns, lg, offset);\n
}\n
\n
