value.string:
//\n
// Input groups\n
// --------------------------------------------------\n
\n
// Base styles\n
// -------------------------\n
.input-group {\n
  position: relative; // For dropdowns\n
  display: table;\n
  border-collapse: separate; // prevent input groups from inheriting border styles from table cells when placed within a table\n
\n
  // Undo padding and float of grid classes\n
  &[class*="col-"] {\n
    float: none;\n
    padding-left: 0;\n
    padding-right: 0;\n
  }\n
\n
  .form-control {\n
    width: 100%;\n
    margin-bottom: 0;\n
  }\n
}\n
\n
// Sizing options\n
//\n
// Remix the default form control sizing classes into new ones for easier\n
// manipulation.\n
\n
.input-group-lg > .form-control,\n
.input-group-lg > .input-group-addon,\n
.input-group-lg > .input-group-btn > .btn { .input-lg(); }\n
.input-group-sm > .form-control,\n
.input-group-sm > .input-group-addon,\n
.input-group-sm > .input-group-btn > .btn { .input-sm(); }\n
\n
\n
// Display as table-cell\n
// -------------------------\n
.input-group-addon,\n
.input-group-btn,\n
.input-group .form-control {\n
  display: table-cell;\n
\n
  &:not(:first-child):not(:last-child) {\n
    border-radius: 0;\n
  }\n
}\n
// Addon and addon wrapper for buttons\n
.input-group-addon,\n
.input-group-btn {\n
  width: 1%;\n
  white-space: nowrap;\n
  vertical-align: middle; // Match the inputs\n
}\n
\n
// Text input groups\n
// -------------------------\n
.input-group-addon {\n
  padding: @padding-base-vertical @padding-base-horizontal;\n
  font-size: @font-size-base;\n
  font-weight: normal;\n
  line-height: 1;\n
  color: @input-color;\n
  text-align: center;\n
  background-color: @input-group-addon-bg;\n
  border: 1px solid @input-group-addon-border-color;\n
  border-radius: @border-radius-base;\n
\n
  // Sizing\n
  &.input-sm {\n
    padding: @padding-small-vertical @padding-small-horizontal;\n
    font-size: @font-size-small;\n
    border-radius: @border-radius-small;\n
  }\n
  &.input-lg {\n
    padding: @padding-large-vertical @padding-large-horizontal;\n
    font-size: @font-size-large;\n
    border-radius: @border-radius-large;\n
  }\n
\n
  // Nuke default margins from checkboxes and radios to vertically center within.\n
  input[type="radio"],\n
  input[type="checkbox"] {\n
    margin-top: 0;\n
  }\n
}\n
\n
// Reset rounded corners\n
.input-group .form-control:first-child,\n
.input-group-addon:first-child,\n
.input-group-btn:first-child > .btn,\n
.input-group-btn:first-child > .dropdown-toggle,\n
.input-group-btn:last-child > .btn:not(:last-child):not(.dropdown-toggle) {\n
  .border-right-radius(0);\n
}\n
.input-group-addon:first-child {\n
  border-right: 0;\n
}\n
.input-group .form-control:last-child,\n
.input-group-addon:last-child,\n
.input-group-btn:last-child > .btn,\n
.input-group-btn:last-child > .dropdown-toggle,\n
.input-group-btn:first-child > .btn:not(:first-child) {\n
  .border-left-radius(0);\n
}\n
.input-group-addon:last-child {\n
  border-left: 0;\n
}\n
\n
// Button input groups\n
// -------------------------\n
.input-group-btn {\n
  position: relative;\n
  white-space: nowrap;\n
\n
  // Negative margin to only have a 1px border between the two\n
  &:first-child > .btn {\n
    margin-right: -1px;\n
  }\n
  &:last-child > .btn {\n
    margin-left: -1px;\n
  }\n
}\n
.input-group-btn > .btn {\n
  position: relative;\n
  // Jankily prevent input button groups from wrapping\n
  + .btn {\n
    margin-left: -4px;\n
  }\n
  // Bring the "active" button to the front\n
  &:hover,\n
  &:active {\n
    z-index: 2;\n
  }\n
}\n
