// Common styles\n
// -------------------------\n
\n
// Clear the floats\n
.media,\n
.media-body {\n
  overflow: hidden;\n
  zoom: 1;\n
}\n
\n
// Proper spacing between instances of .media\n
.media,\n
.media .media {\n
  margin-top: 15px;\n
}\n
.media:first-child {\n
  margin-top: 0;\n
}\n
\n
// For images and videos, set to block\n
.media-object {\n
  display: block;\n
}\n
\n
// Reset margins on headings for tighter default spacing\n
.media-heading {\n
  margin: 0 0 5px;\n
}\n
\n
\n
// Media image alignment\n
// -------------------------\n
\n
.media {\n
  > .pull-left {\n
    margin-right: 10px;\n
  }\n
  > .pull-right {\n
    margin-left: 10px;\n
  }\n
}\n
\n
\n
// Media list variation\n
// -------------------------\n
\n
// Undo default ul/ol styles\n
.media-list {\n
  padding-left: 0;\n
  list-style: none;\n
}\n
