@media print {\n
\n
  * {\n
    text-shadow: none !important;\n
    color: #000 !important; // Black prints faster: h5bp.com/s\n
    background: transparent !important;\n
    box-shadow: none !important;\n
  }\n
\n
  a,\n
  a:visited {\n
    text-decoration: underline;\n
  }\n
\n
  a[href]:after {\n
    content: " (" attr(href) ")";\n
  }\n
\n
  abbr[title]:after {\n
    content: " (" attr(title) ")";\n
  }\n
\n
  // Don\'t show links for images, or javascript/internal links\n
  a[href^="javascript:"]:after,\n
  a[href^="#"]:after {\n
    content: "";\n
  }\n
\n
  pre,\n
  blockquote {\n
    border: 1px solid #999;\n
    page-break-inside: avoid;\n
  }\n
\n
  thead {\n
    display: table-header-group; // h5bp.com/t\n
  }\n
\n
  tr,\n
  img {\n
    page-break-inside: avoid;\n
  }\n
\n
  img {\n
    max-width: 100% !important;\n
  }\n
\n
  @page {\n
    margin: 2cm .5cm;\n
  }\n
\n
  p,\n
  h2,\n
  h3 {\n
    orphans: 3;\n
    widows: 3;\n
  }\n
\n
  h2,\n
  h3 {\n
    page-break-after: avoid;\n
  }\n
\n
  // Chrome (OSX) fix for https://github.com/twbs/bootstrap/issues/11245\n
  // Once fixed, we can just straight up remove this.\n
  select {\n
    background: #fff !important;\n
  }\n
\n
  // Bootstrap components\n
  .navbar {\n
    display: none;\n
  }\n
  .table {\n
    td,\n
    th {\n
      background-color: #fff !important;\n
    }\n
  }\n
  .btn,\n
  .dropup > .btn {\n
    > .caret {\n
      border-top-color: #000 !important;\n
    }\n
  }\n
  .label {\n
    border: 1px solid #000;\n
  }\n
\n
  .table {\n
    border-collapse: collapse !important;\n
  }\n
  .table-bordered {\n
