//\n
// Progress bars\n
// --------------------------------------------------\n
\n
\n
// Bar animations\n
// -------------------------\n
\n
// WebKit\n
@-webkit-keyframes progress-bar-stripes {\n
  from  { background-position: 40px 0; }\n
  to    { background-position: 0 0; }\n
}\n
\n
// Spec and IE10+\n
@keyframes progress-bar-stripes {\n
  from  { background-position: 40px 0; }\n
  to    { background-position: 0 0; }\n
}\n
\n
\n
\n
// Bar itself\n
// -------------------------\n
\n
// Outer container\n
.progress {\n
  overflow: hidden;\n
  height: @line-height-computed;\n
  margin-bottom: @line-height-computed;\n
  background-color: @progress-bg;\n
  border-radius: @border-radius-base;\n
  .box-shadow(inset 0 1px 2px rgba(0,0,0,.1));\n
}\n
\n
// Bar of progress\n
.progress-bar {\n
  float: left;\n
  width: 0%;\n
  height: 100%;\n
  font-size: @font-size-small;\n
  line-height: @line-height-computed;\n
  color: @progress-bar-color;\n
  text-align: center;\n
  background-color: @progress-bar-bg;\n
  .box-shadow(inset 0 -1px 0 rgba(0,0,0,.15));\n
  .transition(width .6s ease);\n
}\n
\n
// Striped bars\n
.progress-striped .progress-bar {\n
  #gradient > .striped();\n
  background-size: 40px 40px;\n
}\n
\n
// Call animation for the active one\n
.progress.active .progress-bar {\n
  .animation(progress-bar-stripes 2s linear infinite);\n
}\n
\n
\n
\n
// Variations\n
// -------------------------\n
\n
.progress-bar-success {\n
  .progress-bar-variant(@progress-bar-success-bg);\n
}\n
\n
.progress-bar-info {\n
  .progress-bar-variant(@progress-bar-info-bg);\n
}\n
\n
.progress-bar-warning {\n
  .progress-bar-variant(@progress-bar-warning-bg);\n
}\n
\n
.progress-bar-danger {\n
  .progress-bar-variant(@progress-bar-danger-bg);\n
}\n
