 Ext.define('Ext.env.Browser', {
 requires: ['Ext.Version'],

 statics: {
 browserNames: {
 ie: 'IE',
 firefox: 'Firefox',
 safari: 'Safari',
 chrome: 'Chrome',
 opera: 'Opera',
 dolfin: 'Dolfin',
 webosbrowser: 'webOSBrowser',
 chromeMobile: 'ChromeMobile',
 silk: 'Silk',
 other: 'Other'
 },
 engineNames: {
 webkit: 'WebKit',
 gecko: 'Gecko',
 presto: 'Presto',
 trident: 'Trident',
 other: 'Other'
 },
 enginePrefixes: {
 webkit: 'AppleWebKit/',
 gecko: 'Gecko/',
 presto: 'Presto/',
 trident: 'Trident/'
 },
 browserPrefixes: {
 ie: 'MSIE ',
 firefox: 'Firefox/',
 chrome: 'Chrome/',
 safari: 'Version/',
 opera: 'Opera/',
 dolfin: 'Dolfin/',
 webosbrowser: 'wOSBrowser/',
 chromeMobile: 'CrMo/',
 silk: 'Silk/'
 }
 },

