 */
 unBefore: 'removeBeforeListener',
 /**
 * @method
 * Alias for {@link #removeAfterListener}.
 * @inheritdoc Ext.mixin.Observable#removeAfterListener
 */
 unAfter: 'removeAfterListener'
 });

