 Ext.define('Ext.fx.layout.card.Abstract', {
 extend: 'Ext.Evented',
 isAnimation: true,

 config: {
 direction: 'left',

 duration: null,

 reverse: null,

 layout: null
 },

 updateLayout: function() {
 this.enable();
 },

 enable: function() {
 var layout = this.getLayout();

 if (layout) {
 layout.onBefore('activeitemchange', 'onActiveItemChange', this);
 }
 },

 disable: function() {
 var layout = this.getLayout();

 if (this.isAnimating) {
 this.stopAnimation();
 }

 if (layout) {
 layout.unBefore('activeitemchange', 'onActiveItemChange', this);
 }
 },

 onActiveItemChange: Ext.emptyFn,

 destroy: function() {
 var layout = this.getLayout();

 if (this.isAnimating) {
 this.stopAnimation();
 }

 if (layout) {
 layout.unBefore('activeitemchange', 'onActiveItemChange', this);
 }
 this.setLayout(null);
 }
 });

 /**
 * @private
 */
 Ext.define('Ext.fx.State', {

 isAnimatable: {
 'background-color' : true,
 'background-image' : true,
 'background-position': true,
 'border-bottom-color': true,
 'border-bottom-width': true,
 'border-color' : true,
 'border-left-color' : true,
 'border-left-width' : true,
 'border-right-color' : true,
 'border-right-width' : true,
 'border-spacing' : true,
 'border-top-color' : true,
 'border-top-width' : true,
 'border-width' : true,
 'bottom' : true,
 'color' : true,
 'crop' : true,
 'font-size' : true,
 'font-weight' : true,
 'height' : true,
 'left' : true,
 'letter-spacing' : true,
 'line-height' : true,
 'margin-bottom' : true,
 'margin-left' : true,
 'margin-right' : true,
 'margin-top' : true,
 'max-height' : true,
 'max-width' : true,
 'min-height' : true,
 'min-width' : true,
 'opacity' : true,
 'outline-color' : true,
 'outline-offset' : true,
 'outline-width' : true,
 'padding-bottom' : true,
 'padding-left' : true,
 'padding-right' : true,
 'padding-top' : true,
 'right' : true,
 'text-indent' : true,
 'text-shadow' : true,
 'top' : true,
 'vertical-align' : true,
 'visibility' : true,
 'width' : true,
 'word-spacing' : true,
 'z-index' : true,
 'zoom' : true,
 'transform' : true
 },

 constructor: function(data) {
 this.data = {};

 this.set(data);
 },

 setConfig: function(data) {
 this.set(data);

 return this;
 },

 setRaw: function(data) {
 this.data = data;

 return this;
 },

 clear: function() {
 return this.setRaw({});
 },

 setTransform: function(name, value) {
 var data = this.data,
 isArray = Ext.isArray(value),
 transform = data.transform,
 ln, key;

 if (!transform) {
 transform = data.transform = {
 translateX: 0,
 translateY: 0,
 translateZ: 0,
 scaleX: 1,
 scaleY: 1,
 scaleZ: 1,
 rotate: 0,
 rotateX: 0,
 rotateY: 0,
 rotateZ: 0,
 skewX: 0,
 skewY: 0
 };
 }

 if (typeof name == 'string') {
 switch (name) {
 case 'translate':
 if (isArray) {
 ln = value.length;

 if (ln == 0) { break; }

 transform.translateX = value[0];

 if (ln == 1) { break; }

 transform.translateY = value[1];

 if (ln == 2) { break; }

 transform.translateZ = value[2];
 }
 else {
 transform.translateX = value;
 }
 break;

 case 'rotate':
 if (isArray) {
 ln = value.length;

 if (ln == 0) { break; }

 transform.rotateX = value[0];

 if (ln == 1) { break; }

 transform.rotateY = value[1];

 if (ln == 2) { break; }

 transform.rotateZ = value[2];
 }
 else {
 transform.rotate = value;
 }
 break;


 case 'scale':
 if (isArray) {
 ln = value.length;

 if (ln == 0) { break; }

 transform.scaleX = value[0];

 if (ln == 1) { break; }

 transform.scaleY = value[1];

 if (ln == 2) { break; }

 transform.scaleZ = value[2];
 }
 else {
 transform.scaleX = value;
 transform.scaleY = value;
 }
 break;

 case 'skew':
 if (isArray) {
 ln = value.length;

 if (ln == 0) { break; }

 transform.skewX = value[0];

 if (ln == 1) { break; }

 transform.skewY = value[1];
 }
 else {
 transform.skewX = value;
 }
 break;

 default:
 transform[name] = value;
 }
 }
 else {
 for (key in name) {
 if (name.hasOwnProperty(key)) {
 value = name[key];

 this.setTransform(key, value);
 }
 }
 }
 },

 set: function(name, value) {
 var data = this.data,
 key;

 if (typeof name != 'string') {
 for (key in name) {
 value = name[key];

 if (key === 'transform') {
 this.setTransform(value);
 }
 else {
 data[key] = value;
 }
 }
 }
 else {
 if (name === 'transform') {
 this.setTransform(value);
 }
 else {
 data[name] = value;
 }
 }

 return this;
 },

 unset: function(name) {
 var data = this.data;

 if (data.hasOwnProperty(name)) {
 delete data[name];
 }

 return this;
 },

 getData: function() {
 return this.data;
 }
 });



 /**
 * @private
 */
 Ext.define('Ext.fx.animation.Abstract', {

 extend: 'Ext.Evented',

 isAnimation: true,

 requires: [
 'Ext.fx.State'
 ],

 config: {
 name: '',

 element: null,

 /**
 * @cfg
 * Before configuration.
 */
 before: null,

 from: {},

 to: {},

 after: null,

 states: {},

 duration: 300,

 /**
 * @cfg
 * Easing type.
 */
 easing: 'linear',

 iteration: 1,

 direction: 'normal',

 delay: 0,

 onBeforeStart: null,

 onEnd: null,

 onBeforeEnd: null,

 scope: null,

 reverse: null,

 preserveEndState: false,

 replacePrevious: true
 },

 STATE_FROM: '0%',

 STATE_TO: '100%',

 DIRECTION_UP: 'up',

 DIRECTION_DOWN: 'down',

 DIRECTION_LEFT: 'left',

 DIRECTION_RIGHT: 'right',

 stateNameRegex: /^(?:[\d\.]+)%$/,

 constructor: function() {
 this.states = {};

 this.callParent(arguments);

 return this;
 },

 applyElement: function(element) {
 return Ext.get(element);
 },

 applyBefore: function(before, current) {
 if (before) {
 return Ext.factory(before, Ext.fx.State, current);
 }
 },

 applyAfter: function(after, current) {
 if (after) {
 return Ext.factory(after, Ext.fx.State, current);
 }
 },

 setFrom: function(from) {
 return this.setState(this.STATE_FROM, from);
 },

 setTo: function(to) {
 return this.setState(this.STATE_TO, to);
 },

 getFrom: function() {
 return this.getState(this.STATE_FROM);
 },

 getTo: function() {
 return this.getState(this.STATE_TO);
 },

 setStates: function(states) {
 var validNameRegex = this.stateNameRegex,
 name;

 for (name in states) {
 if (validNameRegex.test(name)) {
 this.setState(name, states[name]);
 }
 }

 return this;
 },

 getStates: function() {
 return this.states;
 },

 stop: function() {
 this.fireEvent('stop', this);
 },

 destroy: function() {
 this.stop();
 this.callParent();
 },

 setState: function(name, state) {
 var states = this.getStates(),
 stateInstance;

 stateInstance = Ext.factory(state, Ext.fx.State, states[name]);

 if (stateInstance) {
 states[name] = stateInstance;
 }
 //<debug error>
 else if (name === this.STATE_TO) {
 Ext.Logger.error("Setting and invalid '100%' / 'to' state of: " + state);
 }
 //</debug>

 return this;
 },

 getState: function(name) {
 return this.getStates()[name];
 },

 getData: function() {
 var states = this.getStates(),
 statesData = {},
 before = this.getBefore(),
 after = this.getAfter(),
 from = states[this.STATE_FROM],
 to = states[this.STATE_TO],
 fromData = from.getData(),
 toData = to.getData(),
 data, name, state;

 for (name in states) {
 if (states.hasOwnProperty(name)) {
 state = states[name];
 data = state.getData();
 statesData[name] = data;
 }
 }

 if (Ext.os.is.Android2) {
 statesData['0.0001%'] = fromData;
 }

 return {
 before: before ? before.getData() : {},
 after: after ? after.getData() : {},
 states: statesData,
 from: fromData,
 to: toData,
 duration: this.getDuration(),
 iteration: this.getIteration(),
 direction: this.getDirection(),
 easing: this.getEasing(),
 delay: this.getDelay(),
 onEnd: this.getOnEnd(),
 onBeforeEnd: this.getOnBeforeEnd(),
 onBeforeStart: this.getOnBeforeStart(),
 scope: this.getScope(),
 preserveEndState: this.getPreserveEndState(),
 replacePrevious: this.getReplacePrevious()
 };
 }
 });

 /**
 * @private
 */
 Ext.define('Ext.fx.animation.Slide', {

 extend: 'Ext.fx.animation.Abstract',

 alternateClassName: 'Ext.fx.animation.SlideIn',

 alias: ['animation.slide', 'animation.slideIn'],

 config: {
 /**
 * @cfg {String} direction The direction of which the slide animates
 * @accessor
 */
 direction: 'left',

 /**
 * @cfg {Boolean} out True if you want to make this animation slide out, instead of slide in.
 * @accessor
 */
 out: false,

 /**
 * @cfg {Number} offset The offset that the animation should go offscreen before entering (or when exiting)
 * @accessor
 */
 offset: 0,

 /**
 * @cfg
 * @inheritdoc
 */
 easing: 'auto',

 containerBox: 'auto',

 elementBox: 'auto',

 isElementBoxFit: true,

 useCssTransform: true
 },

 reverseDirectionMap: {
 up: 'down',
 down: 'up',
 left: 'right',
 right: 'left'
 },

 applyEasing: function(easing) {
 if (easing === 'auto') {
 return 'ease-' + ((this.getOut()) ? 'in' : 'out');
 }

 return easing;
 },

 getContainerBox: function() {
 var box = this._containerBox;

 if (box === 'auto') {
 box = this.getElement().getParent().getPageBox();
 }

 return box;
 },

 getElementBox: function() {
 var box = this._elementBox;

 if (this.getIsElementBoxFit()) {
 return this.getContainerBox();
 }

 if (box === 'auto') {
 box = this.getElement().getPageBox();
 }

 return box;
 },

 getData: function() {
 var elementBox = this.getElementBox(),
 containerBox = this.getContainerBox(),
 box = elementBox ? elementBox : containerBox,
 from = this.getFrom(),
 to = this.getTo(),
 out = this.getOut(),
 offset = this.getOffset(),
 direction = this.getDirection(),
 useCssTransform = this.getUseCssTransform(),
 reverse = this.getReverse(),
 translateX = 0,
 translateY = 0,
 fromX, fromY, toX, toY;

 if (reverse) {
 direction = this.reverseDirectionMap[direction];
 }

 switch (direction) {
 case this.DIRECTION_UP:
 if (out) {
 translateY = containerBox.top - box.top - box.height - offset;
 }
 else {
 translateY = containerBox.bottom - box.bottom + box.height + offset;
 }

 break;

 case this.DIRECTION_DOWN:
 if (out) {
 translateY = containerBox.bottom - box.bottom + box.height + offset;
 }
 else {
 translateY = containerBox.top - box.height - box.top - offset;
 }

 break;

 case this.DIRECTION_RIGHT:
 if (out) {
 translateX = containerBox.right - box.right + box.width + offset;
 }
 else {
 translateX = containerBox.left - box.left - box.width - offset;
 }

 break;

 case this.DIRECTION_LEFT:
 if (out) {
 translateX = containerBox.left - box.left - box.width - offset;
 }
 else {
 translateX = containerBox.right - box.right + box.width + offset;
 }

 break;
 }

 fromX = (out) ? 0 : translateX;
 fromY = (out) ? 0 : translateY;

 if (useCssTransform) {
 from.setTransform({
 translateX: fromX,
 translateY: fromY
 });
 }
 else {
 from.set('left', fromX);
 from.set('top', fromY);
 }

 toX = (out) ? translateX : 0;
 toY = (out) ? translateY : 0;

 if (useCssTransform) {
 to.setTransform({
 translateX: toX,
 translateY: toY
 });
 }
 else {
 to.set('left', toX);
 to.set('top', toY);
 }

 return this.callParent(arguments);
 }
 });

 /**
 * @private
 */
 Ext.define('Ext.fx.animation.SlideOut', {
 extend: 'Ext.fx.animation.Slide',
 alias: ['animation.slideOut'],

 config: {
 // @hide
 out: true
 }
 });

 /**
