 Ext.define('Ext.fx.animation.Fade', {
 extend: 'Ext.fx.animation.Abstract',

 alternateClassName: 'Ext.fx.animation.FadeIn',

 alias: ['animation.fade', 'animation.fadeIn'],

 config: {
 /**
 * @cfg {Boolean} out True if you want to make this animation fade out, instead of fade in.
 * @accessor
 */

 out: false,

 before: {
 display: null,
 opacity: 0
 },

 after: {
 opacity: null
 },
 reverse: null
 },

 updateOut: function(newOut) {
 var to = this.getTo(),
 from = this.getFrom();

 if (newOut) {
 from.set('opacity', 1);
 to.set('opacity', 0);
 } else {
 from.set('opacity', 0);
 to.set('opacity', 1);
 }
 }
 });

 /**
 * @private
 */
 Ext.define('Ext.fx.animation.FadeOut', {
 extend: 'Ext.fx.animation.Fade',
 alias: 'animation.fadeOut',

 config: {
 // @hide
 out: true,

 before: {}
 }
 });

 /**
 * @private
 */
 Ext.define('Ext.fx.animation.Flip', {
 extend: 'Ext.fx.animation.Abstract',

 alias: 'animation.flip',

 config: {
 easing: 'ease-in',

 /**
 * @cfg {String} direction The direction of which the slide animates
 * @accessor
 */
 direction: 'right',

 half: false,

 out: null
 },

 getData: function() {
 var from = this.getFrom(),
 to = this.getTo(),
 direction = this.getDirection(),
 out = this.getOut(),
 half = this.getHalf(),
 rotate = (half) ? 90 : 180,
 fromScale = 1,
 toScale = 1,
 fromRotateX = 0,
 fromRotateY = 0,
 toRotateX = 0,
 toRotateY = 0;


 if (out) {
 toScale = 0.8;
 }
 else {
 fromScale = 0.8;
 }

 switch (direction) {
 case this.DIRECTION_UP:
 if (out) {
 toRotateX = rotate;
 }
 else {
 fromRotateX = -rotate;
 }
 break;

 case this.DIRECTION_DOWN:
 if (out) {
 toRotateX = -rotate;
 }
 else {
 fromRotateX = rotate;
 }
 break;

 case this.DIRECTION_RIGHT:
 if (out) {
 toRotateY = -rotate;
 }
 else {
 fromRotateY = rotate;
 }
 break;

 case this.DIRECTION_LEFT:
 if (out) {
 toRotateY = -rotate;
 }
 else {
 fromRotateY = rotate;
 }
 break;
 }

 from.setTransform({
 rotateX: fromRotateX,
 rotateY: fromRotateY,
 scale: fromScale
 });

 to.setTransform({
 rotateX: toRotateX,
 rotateY: toRotateY,
 scale: toScale
 });

 return this.callParent(arguments);
 }
 });

 /**
 * @private
 */
 Ext.define('Ext.fx.animation.Pop', {
 extend: 'Ext.fx.animation.Abstract',

 alias: ['animation.pop', 'animation.popIn'],

 alternateClassName: 'Ext.fx.animation.PopIn',

 config: {
 /**
 * @cfg {Boolean} out True if you want to make this animation pop out, instead of pop in.
 * @accessor
 */
 out: false,

 before: {
 display: null,
 opacity: 0
 },
 after: {
 opacity: null
 }
 },

 getData: function() {
 var to = this.getTo(),
 from = this.getFrom(),
 out = this.getOut();

 if (out) {
 from.set('opacity', 1);
 from.setTransform({
 scale: 1
 });

 to.set('opacity', 0);
 to.setTransform({
 scale: 0
 });
 }
 else {
 from.set('opacity', 0);
 from.setTransform({
 scale: 0
 });

 to.set('opacity', 1);
 to.setTransform({
 scale: 1
 });
 }

 return this.callParent(arguments);
 }
 });

