 rotateCenterY: "rotationCenterY",
 scaleX: "scalingX",
 scaleY: "scalingY",
 scaleCenterX: "scalingCenterX",
 scaleCenterY: "scalingCenterY"
 },

 defaults: {
 hidden: false,
 zIndex: 0,

 strokeStyle: "none",
 fillStyle: "none",
 lineWidth: 1,
 lineCap: "butt",
 lineJoin: "miter",
 miterLimit: 1,

 shadowColor: "none",
 shadowOffsetX: 0,
 shadowOffsetY: 0,
 shadowBlur: 0,

 globalAlpha: 1,
 strokeOpacity: 1,
 fillOpacity: 1,
 transformFillStroke: false,

 translationX: 0,
 translationY: 0,
 rotationRads: 0,
 rotationCenterX: null,
 rotationCenterY: null,
 scalingX: 1,
 scalingY: 1,
 scalingCenterX: null,
 scalingCenterY: null
 },

 dirtyTriggers: {
 hidden: "canvas",
 zIndex: "zIndex",

 globalAlpha: "canvas",
 globalCompositeOperation: "canvas",

 transformFillStroke: "canvas",
 strokeStyle: "canvas",
 fillStyle: "canvas",
 strokeOpacity: "canvas",
 fillOpacity: "canvas",

 lineWidth: "canvas",
