 },
 defaults: {
 dataY: null,
 dataX: null,
 dataMinX: 0,
 dataMaxX: 1,
 dataMinY: 0,
 dataMaxY: 1,
 centerX: 0,
 centerY: 0,
 startAngle: 0,
 endAngle: Math.PI,
 startRho: 0,
 endRho: 150,
 baseRotation: 0,
 labels: null,
 labelOverflowPadding: 10
 },
