  // Require Underscore, if we\'re on the server, and it\'s not already present.\n
  var _ = root._;\n
  if (!_ && (typeof require !== \'undefined\')) _ = require(\'underscore\');\n
\n
  // For Backbone\'s purposes, jQuery, Zepto, or Ender owns the `$` variable.\n
