 (function (window, document, rJS, URI, RSVP, UriTemplate) {
 "use strict";



 rJS(window)
 /////////////////////////////////////////////////////////////////
 // ready
 /////////////////////////////////////////////////////////////////
 // Init local properties
 .ready(function (g) {
 g.props = {
 cell_gadget_list: [],
 listbox_uid_dict: {}
 };
 })

 // Assign the element to a variable
 .ready(function (g) {
 return g.getElement()
 .push(function (element) {
 g.props.element = element;
 });
 })


 //////////////////////////////////////////////
 // acquired method
 //////////////////////////////////////////////
 .declareAcquiredMethod("jio_allDocs", "jio_allDocs")
 .declareAcquiredMethod("translateHtml", "translateHtml")
 .declareAcquiredMethod("getUrlFor", "getUrlFor")
 .declareAcquiredMethod("getUrlParameter", "getUrlParameter")
 .declareAcquiredMethod("redirect", "redirect")
 .declareAcquiredMethod("getFieldTypeGadgetUrl", "getFieldTypeGadgetUrl")
 .declareAcquiredMethod("triggerSortEditor", "triggerSortEditor")
 .declareAcquiredMethod("showQueryError", "showQueryError")
 //////////////////////////////////////////////
 // initialize the gadget content
 //////////////////////////////////////////////
 .declareMethod('render', function (options) {
 var gadget = this,
 thead = gadget.props.element.querySelector('thead'),
 field_json = options.field_json,
 tr = document.createElement("tr"),
 document_table = gadget.props.element.querySelector('.document_table'),
 th,
 tmp,
 length_table = field_json.column_list.length,
 j,
 i;

 gadget.props.field_json = field_json;
 gadget.props.extended_search = options.extended_search;
 gadget.props.domain_tree = options.domain_tree;
 gadget.props.jio_key = options.jio_key;
 gadget.props.view = options.view;
 gadget.props.hide_item = options.hide_item;
 gadget.props.sort_list = [];
 if (options.sort_list) {
 gadget.props.sort_list = Object.keys(options.sort_list).map(function (key) {return options.sort_list[key]; });
 }

 if (field_json.show_anchor) {
 th = document.createElement("th");
 th.textContent = "Jump";
 th.setAttribute("data-i18n", "Jump");
 tr.appendChild(th);
 }
 for (i = 0; i < length_table; i += 1) {
 th = document.createElement("th");
 th.textContent = field_json.column_list[i][1];
 for (j = 0; j < gadget.props.sort_list.length; j += 1) {
 tmp = gadget.props.sort_list[j].split(",");
 if (tmp[0] === gadget.props.field_json.column_list[i][0]) {
 if (tmp[1] === "ascending") {
 th.setAttribute("class", "ui-icon-arrow-up");
 } else {
 th.setAttribute("class", "ui-icon-arrow-down");
 }
 break;
 }
 }
 th.setAttribute("data-i18n", field_json.column_list[i][1]);
 tr.appendChild(th);
 }
 thead.appendChild(tr);
 return new RSVP.Queue()
 .push(function () {
 // XXX Fix in case of multiple listboxes
 return gadget.getUrlParameter(field_json.key + '_begin_from');
 })
 .push(function (result) {
 if (result === undefined) {
 result = '0';
 }
 gadget.props.begin_from = parseInt(result, 10) || 0;
 return gadget.translateHtml(document_table.innerHTML);
 })
 .push(function (my_translate_html) {
 document_table.innerHTML = my_translate_html;
 });
 })

 //////////////////////////////////////////////
 // render the listbox in an asynchronous way
 //////////////////////////////////////////////
 .declareService(function () {
 var gadget = this,
 props = gadget.props,
 field_json = props.field_json,
 begin_from = props.begin_from,
 table = props.element.querySelector('table'),
 original_tfoot = props.element.querySelector('tfoot'),
 tbody = document.createElement("tbody"),
 url_query = props.extended_search,
 domain_tree = props.domain_tree,
 domain_tree_query,
 domain_tree_string,
 domain_tree_query_list,
 query_string = "",
 lines = field_json.lines,
 select_list = [],
 dataset,
 counter,
 paging_info_counter,
 hide_item = gadget.props.hide_item,
 sort_list = gadget.props.sort_list || [],
 i,
 j,
 list_method_promise = function () {
 return gadget.jio_allDocs.apply(gadget, arguments);
 };
 if (field_json.query === undefined) {
 original_tfoot.textContent = "Unsupported list method: '" + field_json.list_method + "'";
 return;
 }
 // function buildQueryString(previous, next) {
 // return previous + next[0] + ':= "' + url_query + '" OR ';
 // }
 //rebuild query
 if (url_query) {
 //query_string = field_json.column_list.reduce(buildQueryString, ' AND (').replace(new RegExp("OR " + '$'), ')');
 if (field_json.list_method_template === undefined) {
 query_string = ' AND ' + url_query;
 } else {
 query_string = url_query;
 }
 }

 if (domain_tree) {
 domain_tree_query = QueryFactory.create(domain_tree);
 if (domain_tree_query.query_list) {
 domain_tree_query_list = [domain_tree_query.query_list[0], domain_tree_query.query_list[domain_tree_query.query_list.length - 1]];
 domain_tree_query.query_list = domain_tree_query_list;
 }
 domain_tree_string = Query.objectToSearchText(domain_tree_query);
 if (query_string !== "") {
 query_string += ' AND ' + domain_tree_string;
 } else if (field_json.list_method_template === undefined) {
 query_string = ' AND ' + domain_tree_string;
 } else {
 query_string = domain_tree_string;
 }
 }



 if (hide_item) {
 if (query_string !== "") {
 query_string += ' AND ' + hide_item;
 } else if (field_json.list_method_template === undefined) {
 query_string = ' AND ' + hide_item;
 } else {
 query_string = hide_item;
 }
 }

 for (i = 0; i < field_json.column_list.length; i += 1) {
 select_list.push(field_json.column_list[i][0]);
 }

 select_list.push("uid");

return gadget.jio_allDocs({
 // XXX Not jIO compatible, but until a better api is found...
 "list_method_template": field_json.list_method_template,
 "query": new URI(field_json.query).query(true).query + query_string,
 "limit": [begin_from, lines + 1],
 "select_list": select_list,
 "sort_on": sort_list
 }).push(function (result) {
 var promise_list = [result];
 counter = Math.min(result.data.total_rows, lines);
 for (i = 0; i < counter; i += 1) {
 promise_list.push(
 gadget.getUrlFor({
 command: 'index',
 options: {
 jio_key: result.data.rows[i].id,
 selection_index: begin_from + i,
 query: new URI(field_json.query).query(true).query + query_string,
 list_method_template: field_json.list_method_template
 }
 })
 );
 }
 return RSVP.all(promise_list);

 }).push(function (result_list) {
 var tr,
 th,
 a,
 result = result_list[0],
 tmp_url,
 tmp_div,
 value,
 promise_list = [],
 uid_value,
 uid_value_dict = {};

 function renderSubCell(element, sub_field_json) {
 return gadget.getFieldTypeGadgetUrl(sub_field_json.type)
 .push(function (gadget_url) {
 return gadget.declareGadget(gadget_url, {
 element: element
 });
 })
 .push(function (cell_gadget) {
 gadget.props.cell_gadget_list.push(cell_gadget);
 sub_field_json.editable = sub_field_json.editable && field_json.editable; // XXX 
 return cell_gadget.render({field_json: sub_field_json});
 });
 }

 dataset = result;
 for (i = 0; i < counter; i += 1) {
 tr = document.createElement("tr");
 tr.setAttribute("value", result.data.rows[i].value.uid);
 tmp_url = result_list[i + 1];

 if (field_json.show_anchor) {
 th = document.createElement("th");
 a = document.createElement("a");
 a.href = tmp_url;
 a.className = "ui-link ui-btn ui-corner-all ui-icon-carat-r ui-btn-icon-notext";
 th.appendChild(a);
 tr.appendChild(th);
 }
 for (j = 0; j < field_json.column_list.length; j += 1) {
 th = document.createElement("th");
 value = result.data.rows[i].value[field_json.column_list[j][0]] || "";
 if (value.type === undefined) {
 a = document.createElement("a");
 a.href = tmp_url;
 a.className = "ui-link";
 a.textContent = value;
 th.appendChild(a);
 } else {
 tmp_div = document.createElement("div");
 if (gadget.props.listbox_uid_dict.key === undefined) {
 gadget.props.listbox_uid_dict.key = result.data.rows[i].value["listbox_uid:list"].key;
 gadget.props.listbox_uid_dict.value = [result.data.rows[i].value["listbox_uid:list"].value];
 uid_value_dict[result.data.rows[i].value["listbox_uid:list"].value] = null;
 } else {
 uid_value = result.data.rows[i].value["listbox_uid:list"].value;
 if (!uid_value_dict.hasOwnProperty(uid_value)) {
 uid_value_dict[uid_value] = null;
 gadget.props.listbox_uid_dict.value.push(uid_value);
 }
 }

 promise_list.push(renderSubCell(tmp_div, value));

 //when a sub field is not editable but it's in listbox's editable colummn 
 //then create link, see ListBox.py line 2518
 if (value.editable === 0) {
 a = document.createElement("a");
 a.href = tmp_url;
 a.className = "ui-link";
 a.appendChild(tmp_div);
 th.appendChild(a);
 } else {
 th.appendChild(tmp_div);
 }
 }
 tr.appendChild(th);
 }

 tbody.appendChild(tr);
 }
 return RSVP.all(promise_list);
 }).push(function () {
 var prev_param = {},
 next_param = {};

 function setNext() {
 if (dataset.data.rows.length > lines) {
 next_param[gadget.props.field_json.key + '_begin_from'] = begin_from + lines;
 }
 }

 if (begin_from === 0) {
 setNext();
 } else {
 prev_param[gadget.props.field_json.key + '_begin_from'] = begin_from - lines;
 setNext();
 }
 return RSVP.all([
 gadget.getUrlFor({command: 'change', options: {jio_key: gadget.props.jio_key, "extended_search": gadget.props.extended_search || "",
 "domain_tree": gadget.props.domain_tree || "", "sort_list": gadget.props.sort_list || "",
 "begin_from":prev_param.begin_from}}),
 gadget.getUrlFor({command: 'change', options: {jio_key: gadget.props.jio_key, "extended_search": gadget.props.extended_search || "",
 "domain_tree": gadget.props.domain_tree || "", "sort_list": gadget.props.sort_list || "",
 "begin_from":next_param.begin_from}})
 ]);

 }).push(function (url_list) {
 var fragment = document.createDocumentFragment(),
 tfoot = gadget.__element.querySelector("tfoot"),
 //tfoot = document.createElement("tfoot"),
 tr = document.createElement("tr"),
 th = document.createElement("th"),
 paging_prev = document.createElement("a"),
 paging_next = document.createElement("a"),
 paging_info = document.createElement("span"),
 paging_menu = document.createElement("div"),
 paging_wrap = document.createElement("div"),
 temp = document.createElement("div"),
 temp_table = document.createElement("table"),
 len;

 th.setAttribute("colspan", field_json.column_list.length);
 th.setAttribute("class", "foot_colspan");
 // NOTE: Romain: paging menu with 3+ buttons should have another class name (like ui-paging-grid)
 paging_menu.className = "ui-controlgroup ui-controlgroup-horizontal ui-corner-all ui-paging-menu";
 paging_wrap.className = "ui-controlgroup-controls";

 paging_prev.className = "ui-btn ui-icon-carat-l ui-btn-icon-left responsive ui-first-child";
 paging_prev.textContent = "Previous";
 paging_prev.setAttribute("data-i18n", "Previous");
 paging_prev.href = url_list[0];
 paging_info.className = "paging_info ui-btn ui-disabled";
 if ((begin_from === 0) && (counter === 0)) {
 paging_info.textContent = "No records";
 paging_info.setAttribute("data-i18n", "No Records");
 } else if ((dataset.data.rows.length <= lines) && (begin_from === 0)) {
 paging_info.setAttribute("data-i18n", "[last]Record(s)");
 paging_info_counter = "1 -" + counter;
 paging_info.appendChild(document.createTextNode("Record(s) "));
 } else {
 paging_info.setAttribute("data-i18n", "[node]Record(s)");
 paging_info.appendChild(document.createTextNode("Records "));
 paging_info_counter = (((begin_from + lines) / lines - 1) * lines + 1) + " - " + (((begin_from + lines) / lines - 1) * lines + counter);
 }
 // paging_info.textContent = "Page " + ((begin_from + lines) / lines);
 paging_next.className = "ui-btn ui-icon-carat-r ui-btn-icon-right responsive ui-last-child";
 paging_next.textContent = "Next";
 paging_next.setAttribute("data-i18n", "Next");
 paging_next.href = url_list[1];

 if (begin_from === 0) {
 paging_prev.className += " ui-disabled";
 }
 if (dataset.data.rows.length <= lines) {
 paging_next.className += " ui-disabled";
 }

 paging_wrap.appendChild(paging_prev);
 paging_wrap.appendChild(paging_info);
 paging_wrap.appendChild(paging_next);
 paging_menu.appendChild(paging_wrap);
 th.appendChild(paging_menu);
 tr.appendChild(th);
 tfoot.appendChild(tr);
 fragment.appendChild(tbody);
 fragment.appendChild(tfoot);

 for (len = table.children.length; len > 1; len -= 1) {
 table.removeChild(table.lastChild);
 }
 // NOTE: for passing translations, temp must contain a table
 temp_table.appendChild(fragment);
 temp.appendChild(temp_table);
 table.appendChild(temp.querySelector("tbody"));
 temp.appendChild(document.createElement("tbody"));
 return gadget.translateHtml(temp.innerHTML);
 })
 .push(function (my_translated_html) {
 var temp = document.createElement("table");
 temp.innerHTML = my_translated_html;
 temp.querySelector(".paging_info").appendChild(document.createTextNode(paging_info_counter));
 table.appendChild(temp.querySelector("tfoot"));
 })
 .fail(function (error) {
 if (error.target && error.target.status === 500) {
 return gadget.showQueryError(" Unsupportred Query");
 }
 throw error;
 });
 })
 .declareService(function () {
 var gadget = this,
 sort_form = gadget.props.element.querySelector(".sort_form");
 loopEventListener(
 sort_form,
 "submit",
 false,
 function () {
 gadget.triggerSortEditor();
 }
 );
 })
 .declareService(function () {
 var gadget = this,
 selected_uid_query,
 hide_form = gadget.props.element.querySelector(".hide_form");
 /* hide form */
 loopEventListener(
 hide_form,
 "submit",
 false,
 function () {
 var tr_list = gadget.props.element.querySelector("tbody").querySelectorAll("tr"),
 thead_tr = gadget.props.element.querySelector("thead").querySelector("tr"),
 i,
 th,
 hide_form_elements,
 all_hide_form_elements,
 hide_submit_form = document.createElement("form"),
 hide_show_all = document.createElement("button"),
 query_list = [],
 translate_div,
 colspan = gadget.props.element.querySelector(".foot_colspan"),
 checkbox;
 all_hide_form_elements = gadget.props.element.querySelectorAll(".hide_form_element");
 hide_form_elements = gadget.props.element.querySelectorAll(".hide_form_element:checked");

 if (all_hide_form_elements.length > 0) {
 //if nothing is selected, show all
 if (hide_form_elements.length === 0) {
 colspan.setAttribute("colspan", parseInt(colspan.getAttribute("colspan"), 10) - 1);
 return gadget.redirect({jio_key: gadget.props.jio_key,
 "extended_search": gadget.props.extended_search || "",
 "domain_tree": gadget.props.domain_tree || "",
 "sort_list": gadget.props.sort_list || ""});
 }

 //all selected
 //no need to submit
 if (hide_form_elements.length + 1 === all_hide_form_elements.length) {
 colspan.setAttribute("colspan", parseInt(colspan.getAttribute("colspan"), 10) - 1);
 for (i = 0; i < all_hide_form_elements.length; i += 1) {
 all_hide_form_elements[i].parentElement.removeChild(all_hide_form_elements[i]);
 }
 return;
 }

 //a part is selected
 for (i = 0; i < hide_form_elements.length; i += 1) {
 query_list.push(new SimpleQuery({
 key: "catalog.uid",
 type: "simple",
 value: hide_form_elements[i].parentElement.getAttribute("value")
 }));
 }

 selected_uid_query = new ComplexQuery({
 operator: "OR",
 query_list: query_list,
 type: "complex"
 });

 return gadget.redirect({jio_key: gadget.props.jio_key,
 "extended_search": gadget.props.extended_search || "",
 "domain_tree": gadget.props.domain_tree || "",
 "sort_list": gadget.props.sort_list || "",
 "hide_item": Query.objectToSearchText(selected_uid_query)});
 }

 hide_show_all.setAttribute("type", "submit");
 hide_show_all.setAttribute("data-i18n", "Show All");
 hide_show_all.className = "ui-btn responsive ui-last-child";
 translate_div = document.createElement("div");
 translate_div.appendChild(hide_show_all);
 return new RSVP.Queue()
 .push(function () {
 return gadget.translateHtml(translate_div.innerHTML);
 })
 .push(function (innerHTML) {
 translate_div.innerHTML = innerHTML;
 hide_submit_form.innerHTML = innerHTML;
 th = document.createElement("th");
 colspan.setAttribute("colspan", parseInt(colspan.getAttribute("colspan"), 10) + 1);
 th.setAttribute("class", "hide_form_element");
 th.appendChild(hide_submit_form);
 loopEventListener(
 hide_submit_form,
 "submit",
 false,
 function () {
 return gadget.redirect({jio_key: gadget.props.jio_key,
 "extended_search": gadget.props.extended_search || "",
 "domain_tree": gadget.props.domain_tree || "",
 "sort_list": gadget.props.sort_list || ""});
 }
 );
 thead_tr.insertBefore(th, thead_tr.firstChild);
 for (i = 0; i < tr_list.length; i += 1) {
 checkbox = document.createElement("input");
 checkbox.setAttribute("type", "checkbox");
 checkbox.setAttribute("checked", true);
 checkbox.setAttribute("class", "hide_form_element");
 tr_list[i].insertBefore(checkbox, tr_list[i].firstChild);
 }
 });
 }
 );
 })
 .declareMethod("getContent", function () {
 var form_gadget = this,
 k,
 field_gadget,
 count = form_gadget.props.cell_gadget_list.length,
 data = {},
 queue = new RSVP.Queue();

 function extendData(field_data) {
 var key;
 for (key in field_data) {
 if (field_data.hasOwnProperty(key)) {
 data[key] = field_data[key];
 }
 }
 }

 for (k = 0; k < count; k += 1) {
 field_gadget = form_gadget.props.cell_gadget_list[k];
 // XXX Hack until better defined
 if (field_gadget.getContent !== undefined) {
 queue
 .push(field_gadget.getContent.bind(field_gadget))
 .push(extendData);
 }
 }
 return queue
 .push(function () {
 data[form_gadget.props.listbox_uid_dict.key] = form_gadget.props.listbox_uid_dict.value;
 return data;
 });
 })

 .allowPublicAcquisition("notifyInvalid", function () {
 return;
 })

 .allowPublicAcquisition("notifyValid", function () {
 return;
 });

 }(window, document, rJS, URI, RSVP, UriTemplate));
