 def Object_hasRelation(obj):
 # Check if there is some related objets.
 result = 0
 for o in obj.getIndexableChildValueList():
 for related in obj.portal_categories.getRelatedValueList(o):
 related_url = related.getRelativeUrl()
 if related_url.startswith(obj.getRelativeUrl()):
 continue
 elif related_url.startswith('portal_simulation'):
 continue
 elif related_url.startswith('portal_trash'):
 continue
 else:
 result = 1
 break
 return result


 def isValidID(id):
 return id not in property_id_list

