def sourceSectionSortMethod(a, b):
 if a.getSourceSection():
 return -1 # a defines a source section and wins
 return 1 # a defines no source section and loses


 def destinationSectionSortMethod(a, b):
 if a.getDestinationSection():
 return -1 # a defines a destination section and wins
 return 1 # a defines no destination section and loses


 def getResourcePurchasePriceSortMethod(high_priority_supply_line_list):
 def resourcePurchasePriceSortMethod(a, b):
 if a in high_priority_supply_line_list:
 return -1
 elif b in high_priority_supply_line_list:
 return 1

 if "Purchase" in a.getPortalType():
 if "Purchase" in b.getPortalType():
 return sourceSectionSortMethod(a, b)
 else:
 return -1
 else:
 if "Purchase" in b.getPortalType():
 return 1
 else:
 return destinationSectionSortMethod(a, b)
 return resourcePurchasePriceSortMethod


 def getResourceSalePriceSortMethod(high_priority_supply_line_list):
 def resourceSalePriceSortMethod(a, b):
 if a in high_priority_supply_line_list:
 return -1
 elif b in high_priority_supply_line_list:
 return 1

 if "Sale" in a.getPortalType():
 if "Sale" in b.getPortalType():
 return destinationSectionSortMethod(a, b)
 else:
 return -1
 else:
 if "Sale" in b.getPortalType():
 return 1
 else:
 return sourceSectionSortMethod(a, b)
 return resourceSalePriceSortMethod


 def getRelatedTradeConditionList(trade_condition):
 """Get all trade conditions related to an order.
 trade_condition parameter can be a trade condition or an order/invoice
 """
 related_trade_condition_list = trade_condition.getSpecialiseValueList(
 portal_type=('Sale Trade Condition', 'Purchase Trade Condition'))
 for related_trade_condition in trade_condition.getSpecialiseValueList(
 portal_type=('Sale Trade Condition', 'Purchase Trade Condition')):
 related_trade_condition_list.extend(
 getRelatedTradeConditionList(related_trade_condition))
 return related_trade_condition_list


 try:
 explanation = context.getExplanationValue()
 except AttributeError:
 # Sometime, movements doesn't have an explanation.
 explanation = None

 if explanation is not None:
 explanation_type = explanation.getPortalType()
 high_priority_supply_line_list = []
 if explanation_type in context.getPortalInvoiceTypeList() +\
 context.getPortalOrderTypeList():
 # if there are trade conditions containing supply lines related to that
 # order/invoice, we give high priority to those supply lines
 for trade_condition in getRelatedTradeConditionList(explanation):
 high_priority_supply_line_list.extend(
 list(trade_condition.contentValues(
 portal_type=context.getPortalSupplyPathTypeList(),
 checked_permission='View')))

 # XXX FIXME: Hardcoded values
 if "Purchase" in explanation_type:
 kw['sort_method'] = getResourcePurchasePriceSortMethod(
 high_priority_supply_line_list)
 elif "Sale" in explanation_type:
 kw['sort_method'] = getResourceSalePriceSortMethod(
 high_priority_supply_line_list)

 resource = context.getResourceValue()
 if resource is not None:
 r = resource.getPriceCalculationOperandDict(
 default=default, context=context, **kw)
 return r

 return default
