 function submitAction(form, act) {
 form.action = act;
 form.submit();
 }

 // This function will be called when the user click the save button. As 
 // submitAction function may have changed the action before, it's better to
 // reset the form action to it's original behaviour. This is actually
 // usefull when the user click the back button.
 function clickSaveButton(act) {
 document.forms[0].action = act;
 }


 // The first input element with an "autofocus" class will get the focus,
 // else, nothing done
 function autoFocus() {
 var inputs = document.getElementsByTagName("input");
 var input_to_focus = 0;
 for (i=0;i<inputs.length;i++) {
 if (inputs[i].className == "autofocus") {
 input_to_focus = inputs[i];
 }
 }
 if (input_to_focus != 0) {
 input_to_focus.focus();
 }
 }

 function fixLeftRightHeight(){
 var lh = 0;
 var lfieldset;
 var rh = 0;
 var rfieldset;
 var list=document.getElementsByTagName('fieldset');
 for(i=0; i<list.length; i=i+1){
 list_parts = list[i].id.split('_');
 for(j=1; j<list_parts.length; j=j+1){
 if(list_parts[j] == "left"){
 lfieldset = list[i];
 lh = lfieldset.offsetHeight;
 break;
 }else{
 if(list_parts[j] == "right"){
 rfieldset = list[i];
 rh = rfieldset.offsetHeight;
 break;
 }
 }
 }
 if(lh && rh){
 break;
 }
 }
 if(lh && rh){
 lfieldset.style.height = (lh>rh)? lh+"px" : rh+"px";
 rfieldset.style.height = (lh>rh)? lh+"px" : rh+"px";
 lfieldset.style.borderRightWidth = 0;
 rfieldset.style.borderLeftWidth = 0;
 /* XXX: hardcoded values required unless there is a way to compute "12px"-"1px" in js */
 lfieldset.style.marginRight = "-11px";
 rfieldset.style.marginLeft = "-11px";
 }
 }

 function fixLeftRightHeightAndFocus(fix_height) {
 if (fix_height == 1) {
 fixLeftRightHeight();
 }
 autoFocus();
 }
